import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';
import { useNavigate } from 'react-router-dom';
import { pageContentService } from '../services/pageContentService';

const DashboardLayout = styled.div`
  display: flex;
  min-height: 100vh;
  background: linear-gradient(135deg, #FF6B6B 0%, #845EC2 100%);
`;

const Sidebar = styled.div`
  width: 280px;
  background: rgba(255, 255, 255, 0.95);
  border-right: 1px solid rgba(255, 255, 255, 0.1);
  padding: 20px 0;
  display: flex;
  flex-direction: column;
  backdrop-filter: blur(10px);
`;

const MainContent = styled.div`
  flex: 1;
  padding: 30px;
  overflow-y: auto;
`;

const Logo = styled.div`
  padding: 0 20px 20px;
  border-bottom: 1px solid #eee;
  margin-bottom: 20px;

  img {
    width: 150px;
    height: auto;
  }
`;

const NavSection = styled.div`
  margin-bottom: 20px;
  
  h3 {
    padding: 0 20px;
    font-size: 12px;
    text-transform: uppercase;
    color: #845EC2;
    font-weight: bold;
    margin-bottom: 10px;
  }
`;

const NavItem = styled.div<{ active?: boolean }>`
  padding: 12px 20px;
  cursor: pointer;
  color: ${props => props.active ? '#FF6B6B' : '#333'};
  background: ${props => props.active ? 'rgba(255, 107, 107, 0.1)' : 'transparent'};
  border-left: 3px solid ${props => props.active ? '#FF6B6B' : 'transparent'};
  transition: all 0.2s;
  font-weight: ${props => props.active ? '600' : '400'};

  &:hover {
    background: rgba(255, 107, 107, 0.05);
    color: #FF6B6B;
  }
`;

const TopBar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
  background: rgba(255, 255, 255, 0.95);
  padding: 20px;
  border-radius: 12px;
  backdrop-filter: blur(10px);
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const PageTitle = styled.h1`
  font-size: 24px;
  color: #333;
  margin: 0;
`;

const UserMenu = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  
  .user-info {
    text-align: right;
    
    .name {
      font-size: 14px;
      font-weight: 600;
      color: #333;
    }
    
    .role {
      font-size: 12px;
      color: #666;
    }
  }
`;

// Lista de páginas do dashboard
const pages = [
  { path: 'portal', title: 'Portal', component: 'Portal/index.tsx' },
  { path: 'home', title: 'Página Inicial' },
  { path: 'about', title: 'Sobre Nós' },
  { path: 'services', title: 'Serviços' },
  { path: 'contact', title: 'Contato' }
];

const ContentEditor = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const EditorField = styled.div`
  margin-bottom: 20px;

  label {
    display: block;
    margin-bottom: 8px;
    font-weight: 600;
    color: #333;
  }

  textarea {
    width: 100%;
    min-height: 150px;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 6px;
    font-family: inherit;
    resize: vertical;
  }

  input {
    width: 100%;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 6px;
  }
`;

const SaveButton = styled.button`
  background: #845EC2;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 6px;
  cursor: pointer;
  font-weight: 600;
  transition: all 0.2s;

  &:hover {
    background: #6B4C9F;
  }
`;

const Dashboard = () => {
  const navigate = useNavigate();
  const [selectedSection, setSelectedSection] = useState('dashboard');
  const [isAuthenticated, setIsAuthenticated] = useState(true);
  const [pageContent, setPageContent] = useState({
    title: '',
    briefing: '',
    highlights: ''
  });
  const [isEditing, setIsEditing] = useState(false);
  const [isSaving, setIsSaving] = useState(false);

  // Verifica autenticação uma vez ao montar o componente
  useEffect(() => {
    const isLoggedIn = localStorage.getItem('isAuthenticated');
    setIsAuthenticated(!!isLoggedIn);
  }, []); // Dependência vazia = roda apenas uma vez ao montar

  // Efeito separado para navegação
  useEffect(() => {
    if (!isAuthenticated) {
      navigate('/portal');
    }
  }, [isAuthenticated, navigate]);

  const handleLogout = () => {
    localStorage.removeItem('isAuthenticated');
    setIsAuthenticated(false); // Isso vai disparar o efeito de navegação
  };

  const handleInvestorClick = () => {
    navigate('/investidores');
  };

  const handleContentChange = (field: string, value: string) => {
    setPageContent(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const loadPageContent = async (pagePath: string) => {
    try {
      const content = await pageContentService.getPageContent(pagePath);
      setPageContent(content);
      setIsEditing(true);
    } catch (error) {
      console.error('Error loading page content:', error);
      // Em caso de erro, mantém o conteúdo padrão
    }
  };

  const handleSave = async () => {
    try {
      setIsSaving(true);
      await pageContentService.savePageContent(selectedSection, pageContent);
      console.log('Content saved successfully');
      // Você pode adicionar uma notificação de sucesso aqui
    } catch (error) {
      console.error('Error saving content:', error);
      // Você pode adicionar uma notificação de erro aqui
    } finally {
      setIsSaving(false);
    }
  };

  useEffect(() => {
    if (selectedSection === 'portal') {
      loadPageContent(selectedSection);
    } else {
      setIsEditing(false);
    }
  }, [selectedSection]);

  // Se não estiver autenticado, não renderiza nada
  if (!isAuthenticated) {
    return null;
  }

  return (
    <DashboardLayout>
      <Sidebar>
        <Logo>
          <img src="/logo-davida.svg" alt="Davida Logo" />
        </Logo>

        <NavSection>
          <h3>Geral</h3>
          <NavItem 
            active={selectedSection === 'dashboard'}
            onClick={() => setSelectedSection('dashboard')}
          >
            Dashboard
          </NavItem>
          <NavItem 
            onClick={handleInvestorClick}
          >
            Área do Investidor
          </NavItem>
        </NavSection>

        <NavSection>
          <h3>Páginas</h3>
          {pages.map(page => (
            <NavItem
              key={page.path}
              active={selectedSection === page.path}
              onClick={() => setSelectedSection(page.path)}
            >
              {page.title}
            </NavItem>
          ))}
        </NavSection>

        <NavSection>
          <h3>Configurações</h3>
          <NavItem onClick={handleLogout}>Sair</NavItem>
        </NavSection>
      </Sidebar>

      <MainContent>
        <TopBar>
          <PageTitle>
            {selectedSection === 'dashboard' ? 'Dashboard' : 
              pages.find(p => p.path === selectedSection)?.title || 'Dashboard'}
          </PageTitle>
          <UserMenu>
            <div className="user-info">
              <div className="name">Administrador</div>
              <div className="role">Admin</div>
            </div>
          </UserMenu>
        </TopBar>

        {isEditing && (
          <ContentEditor>
            <EditorField>
              <label>Título da Página</label>
              <input
                type="text"
                value={pageContent.title}
                onChange={(e) => handleContentChange('title', e.target.value)}
              />
            </EditorField>

            <EditorField>
              <label>Texto Principal</label>
              <textarea
                value={pageContent.briefing}
                onChange={(e) => handleContentChange('briefing', e.target.value)}
              />
            </EditorField>

            <EditorField>
              <label>Destaques</label>
              <textarea
                value={pageContent.highlights}
                onChange={(e) => handleContentChange('highlights', e.target.value)}
              />
            </EditorField>

            <SaveButton 
              onClick={handleSave}
              disabled={isSaving}
            >
              {isSaving ? 'Salvando...' : 'Salvar Alterações'}
            </SaveButton>
          </ContentEditor>
        )}
      </MainContent>
    </DashboardLayout>
  );
};

export default Dashboard;
