import React, { useState } from 'react';
import styled from '@emotion/styled';
import { useNavigate } from 'react-router-dom';

const AdminLayout = styled.div`
  display: flex;
  min-height: 100vh;
  background: linear-gradient(135deg, #FF6B6B 0%, #845EC2 100%);
`;

const Sidebar = styled.div`
  width: 280px;
  background: rgba(255, 255, 255, 0.95);
  border-right: 1px solid rgba(255, 255, 255, 0.1);
  padding: 20px 0;
  display: flex;
  flex-direction: column;
  backdrop-filter: blur(10px);
`;

const MainContent = styled.div`
  flex: 1;
  padding: 30px;
  overflow-y: auto;
`;

const Logo = styled.div`
  padding: 0 20px 20px;
  border-bottom: 1px solid #eee;
  margin-bottom: 20px;

  img {
    width: 150px;
    height: auto;
  }
`;

const NavSection = styled.div`
  margin-bottom: 20px;
  
  h3 {
    padding: 0 20px;
    font-size: 12px;
    text-transform: uppercase;
    color: #845EC2;
    font-weight: bold;
    margin-bottom: 10px;
  }
`;

const NavItem = styled.div<{ active?: boolean }>`
  padding: 12px 20px;
  cursor: pointer;
  color: ${props => props.active ? '#FF6B6B' : '#333'};
  background: ${props => props.active ? 'rgba(255, 107, 107, 0.1)' : 'transparent'};
  border-left: 3px solid ${props => props.active ? '#FF6B6B' : 'transparent'};
  transition: all 0.2s;
  font-weight: ${props => props.active ? '600' : '400'};

  &:hover {
    background: rgba(255, 107, 107, 0.05);
    color: #FF6B6B;
  }
`;

const TopBar = styled.div`
  display: flex;
  justify-content: space-between;
  align-items: center;
  margin-bottom: 30px;
  background: rgba(255, 255, 255, 0.95);
  padding: 20px;
  border-radius: 12px;
  backdrop-filter: blur(10px);
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const PageTitle = styled.h1`
  font-size: 24px;
  color: #333;
  margin: 0;
`;

const MetricsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(4, 1fr);
  gap: 20px;
  margin-bottom: 30px;
`;

const MetricCard = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 20px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(10px);

  h3 {
    color: #ffffff;
    font-size: 14px;
    margin-bottom: 8px;
  }

  .value {
    color: #FF6B6B;
    font-size: 28px;
    font-weight: bold;
  }

  .change {
    color: #4CAF50;
    font-size: 12px;
    margin-top: 4px;
  }
`;

const ContentArea = styled.div`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  backdrop-filter: blur(10px);
  padding: 30px;
`;

const EditableField = styled.div`
  margin-bottom: 20px;

  label {
    display: block;
    font-size: 14px;
    color: #666;
    margin-bottom: 8px;
  }

  input, textarea {
    width: 100%;
    padding: 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
    font-family: inherit;
    font-size: 14px;

    &:focus {
      outline: none;
      border-color: #FF6B6B;
      box-shadow: 0 0 0 2px rgba(255, 107, 107, 0.1);
    }
  }

  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const ImagePreview = styled.div`
  margin-top: 10px;
  
  img {
    max-width: 200px;
    height: auto;
    border-radius: 4px;
    border: 1px solid #ddd;
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 20px;
`;

const Button = styled.button`
  padding: 10px 20px;
  border: none;
  border-radius: 6px;
  font-size: 14px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;

  &.primary {
    background: #FF6B6B;
    color: white;

    &:hover {
      background: #ff5252;
    }
  }

  &.secondary {
    background: #eee;
    color: #666;

    &:hover {
      background: #e0e0e0;
    }
  }
`;

const UserMenu = styled.div`
  display: flex;
  align-items: center;
  gap: 10px;
  
  .user-info {
    text-align: right;
    
    .name {
      font-size: 14px;
      font-weight: 600;
      color: #333;
    }
    
    .role {
      font-size: 12px;
      color: #666;
    }
  }
`;

const pages = [
  {
    title: "Portal",
    description: "Voltar para a página inicial do Portal Davida",
    path: "/portal"
  },
  {
    title: "Página Principal",
    description: "Gerencie o conteúdo da página inicial do Davida",
    path: "/"
  },
  {
    title: "Para Mulheres",
    description: "Edite o conteúdo da seção dedicada às mulheres",
    path: "/para-mulheres"
  },
  {
    title: "Funcionalidades",
    description: "Atualize as funcionalidades e recursos do aplicativo",
    path: "/funcionalidades"
  },
  {
    title: "Comunidade",
    description: "Gerencie a seção de comunidade e interações",
    path: "/comunidade"
  },
  {
    title: "Parceiros",
    description: "Administre parcerias e colaborações",
    path: "/parceiros"
  },
  {
    title: "Investidores",
    description: "Gerencie a área dedicada aos investidores",
    path: "/investidores"
  },
  {
    title: "Download",
    description: "Configure a página de download do aplicativo",
    path: "/download"
  },
  {
    title: "Contato",
    description: "Gerencie informações de contato e formulários",
    path: "/contato"
  }
];

const Dashboard = () => {
  const navigate = useNavigate();
  const [selectedSection, setSelectedSection] = useState('dashboard');
  const [metrics] = useState({
    visitors: { value: "12,458", change: "+15%" },
    pageViews: { value: "45,239", change: "+23%" },
    activeUsers: { value: "1,893", change: "+8%" },
    avgTime: { value: "4:32", change: "+12%" }
  });

  const handleLogout = () => {
    navigate('/portal');
  };

  return (
    <AdminLayout>
      <Sidebar>
        <Logo>
          <img src="/logo-davida.svg" alt="Davida Logo" />
        </Logo>

        <NavSection>
          <h3>Geral</h3>
          <NavItem 
            active={selectedSection === 'dashboard'}
            onClick={() => setSelectedSection('dashboard')}
          >
            Dashboard
          </NavItem>
        </NavSection>

        <NavSection>
          <h3>Páginas</h3>
          {pages.map(page => (
            <NavItem
              key={page.path}
              active={selectedSection === page.path}
              onClick={() => setSelectedSection(page.path)}
            >
              {page.title}
            </NavItem>
          ))}
        </NavSection>

        <NavSection>
          <h3>Configurações</h3>
          <NavItem onClick={handleLogout}>Sair</NavItem>
        </NavSection>
      </Sidebar>

      <MainContent>
        <TopBar>
          <PageTitle>
            {selectedSection === 'dashboard' ? 'Dashboard' : 
              pages.find(p => p.path === selectedSection)?.title || 'Dashboard'}
          </PageTitle>
          <UserMenu>
            <div className="user-info">
              <div className="name">Administrador</div>
              <div className="role">Admin</div>
            </div>
          </UserMenu>
        </TopBar>

        {selectedSection === 'dashboard' ? (
          <>
            <MetricsGrid>
              <MetricCard>
                <h3>Visitantes</h3>
                <div className="value">{metrics.visitors.value}</div>
                <div className="change">{metrics.visitors.change}</div>
              </MetricCard>
              <MetricCard>
                <h3>Visualizações</h3>
                <div className="value">{metrics.pageViews.value}</div>
                <div className="change">{metrics.pageViews.change}</div>
              </MetricCard>
              <MetricCard>
                <h3>Usuários Ativos</h3>
                <div className="value">{metrics.activeUsers.value}</div>
                <div className="change">{metrics.activeUsers.change}</div>
              </MetricCard>
              <MetricCard>
                <h3>Tempo Médio</h3>
                <div className="value">{metrics.avgTime.value}</div>
                <div className="change">{metrics.avgTime.change}</div>
              </MetricCard>
            </MetricsGrid>

            <ContentArea>
              <h2>Atividade Recente</h2>
              {/* Aqui você pode adicionar um feed de atividades recentes */}
            </ContentArea>
          </>
        ) : (
          <ContentArea>
            <EditableField>
              <label>Título da Página</label>
              <input type="text" defaultValue={pages.find(p => p.path === selectedSection)?.title} />
            </EditableField>

            <EditableField>
              <label>Descrição</label>
              <textarea defaultValue={pages.find(p => p.path === selectedSection)?.description} />
            </EditableField>

            {/* Aqui você pode adicionar mais campos específicos para cada página */}

            <ButtonGroup>
              <Button className="primary">Salvar Alterações</Button>
              <Button className="secondary">Cancelar</Button>
            </ButtonGroup>
          </ContentArea>
        )}
      </MainContent>
    </AdminLayout>
  );
};

export default Dashboard;
