import React from 'react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet';
import { useContent } from '../../contexts/ContentContext';

const Section = styled.section`
  padding: 80px 20px;
  background: linear-gradient(135deg, #FF6B6B 0%, #845EC2 100%);
  min-height: 100vh;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
`;

const Title = styled(motion.h1)`
  font-size: 2.5rem;
  color: white;
  text-align: center;
  margin-bottom: 20px;
`;

const Subtitle = styled(motion.p)`
  font-size: 1.2rem;
  color: rgba(255, 255, 255, 0.9);
  text-align: center;
  max-width: 800px;
  margin: 0 auto 60px;
  line-height: 1.6;
`;

const FeaturesGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: 30px;
  margin-top: 40px;
`;

const FeatureCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 15px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
  transition: transform 0.3s ease;

  &:hover {
    transform: translateY(-5px);
  }
`;

const FeatureIcon = styled.div`
  font-size: 2.5rem;
  margin-bottom: 20px;
  text-align: center;
`;

const FeatureTitle = styled.h3`
  font-size: 1.5rem;
  color: #333;
  margin-bottom: 15px;
  text-align: center;
`;

const FeatureDescription = styled.p`
  color: #666;
  margin-bottom: 20px;
  line-height: 1.6;
  text-align: center;
`;

const FeatureList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
`;

const FeatureListItem = styled.li`
  color: #666;
  margin-bottom: 10px;
  padding-left: 25px;
  position: relative;

  &:before {
    content: "✓";
    position: absolute;
    left: 0;
    color: #FF6B6B;
  }
`;

const ComingSoonBadge = styled.div`
  background: #FF6B6B;
  color: white;
  padding: 5px 10px;
  border-radius: 20px;
  font-size: 0.8rem;
  display: inline-block;
  margin-top: 15px;
`;

const Funcionalidades: React.FC = () => {
  const { content } = useContent();

  const cardVariants = {
    hidden: { opacity: 0, y: 20 },
    visible: { opacity: 1, y: 0 }
  };

  return (
    <Section>
      <Helmet>
        <title>Funcionalidades do App | Davida - Apoio com Propósito</title>
        <meta name="description" content="Conheça as funcionalidades do Davida: grupos moderados, trilhas espirituais, calendário especial e muito mais para sua jornada de fé e propósito." />
        <meta name="keywords" content="app cristão, grupos moderados, trilhas espirituais, calendário especial, jornada de oração, rede de apoio, apoio feminino cristão" />
        <meta property="og:title" content="Funcionalidades do App | Davida" />
        <meta property="og:description" content="Descubra como o Davida pode te apoiar com grupos moderados, trilhas espirituais, calendário especial e muito mais." />
        <meta property="og:type" content="website" />
        <link rel="canonical" href="https://davida.app/funcionalidades" />
      </Helmet>

      <Container>
        <Title
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8 }}
        >
          {content['features.title'] || 'Funcionalidades do App'}
        </Title>
        <Subtitle
          initial={{ opacity: 0, y: 20 }}
          animate={{ opacity: 1, y: 0 }}
          transition={{ duration: 0.8, delay: 0.2 }}
        >
          {content['features.subtitle'] || 'Ferramentas pensadas com carinho para acompanhar cada momento da sua jornada de fé e propósito'}
        </Subtitle>

        <FeaturesGrid>
          {[
            {
              key: 'groups',
              icon: '👥',
              title: 'Grupos Moderados',
              description: 'Encontre acolhimento em grupos específicos para cada fase da sua jornada',
              features: [
                'Grupos por fase de vida',
                'Moderação especializada',
                'Temas específicos',
                'Encontros por cidade'
              ]
            },
            {
              key: 'spiritual',
              icon: '🙏',
              title: 'Trilhas Espirituais',
              description: 'Fortaleça sua fé com conteúdo personalizado e guiado',
              features: [
                'Devocionais diários',
                'Meditações guiadas',
                'Áudios de apoio',
                'Conteúdo bíblico'
              ]
            },
            {
              key: 'calendar',
              icon: '📅',
              title: 'Calendário Especial',
              description: 'Acompanhe sua jornada com lembretes carinhosos e datas importantes',
              features: [
                'Lembretes de oração',
                'Eventos especiais',
                'Datas comemorativas',
                'Notificações personalizadas'
              ]
            },
            {
              key: 'prayer',
              icon: '✨',
              title: 'Jornada de Oração',
              description: 'Participe de desafios espirituais e fortaleça sua caminhada na fé',
              features: [
                'Desafios diários',
                'Recompensas espirituais',
                'Progresso da jornada',
                'Comunidade orante'
              ]
            },
            {
              key: 'support',
              icon: '💝',
              title: 'Rede de Apoio',
              description: 'Conecte-se com outras mulheres que compartilham sua jornada',
              features: [
                'Mentoria espiritual',
                'Grupos de apoio',
                'Compartilhamento de experiências',
                'Suporte emocional'
              ]
            },
            {
              key: 'ai',
              icon: '🤖',
              title: 'Chat com IA Empática',
              description: 'Suporte personalizado com tecnologia e cuidado humano',
              features: [
                'Respostas personalizadas',
                'Moderadoras treinadas',
                'Disponível 24/7',
                'Apoio especializado'
              ],
              comingSoon: true
            }
          ].map((feature, index) => (
            <FeatureCard
              key={feature.key}
              variants={cardVariants}
              initial="hidden"
              whileInView="visible"
              viewport={{ once: true }}
              transition={{ duration: 0.5, delay: index * 0.1 }}
            >
              <FeatureIcon>
                {content[`features.${feature.key}.icon`] || feature.icon}
              </FeatureIcon>
              <FeatureTitle>
                {content[`features.${feature.key}.title`] || feature.title}
              </FeatureTitle>
              <FeatureDescription>
                {content[`features.${feature.key}.description`] || feature.description}
              </FeatureDescription>
              <FeatureList>
                {feature.features.map((item, itemIndex) => (
                  <FeatureListItem key={itemIndex}>
                    {content[`features.${feature.key}.feature${itemIndex + 1}`] || item}
                  </FeatureListItem>
                ))}
              </FeatureList>
              {feature.comingSoon && (
                <ComingSoonBadge>
                  {content[`features.${feature.key}.comingSoon`] || 'Em breve'}
                </ComingSoonBadge>
              )}
            </FeatureCard>
          ))}
        </FeaturesGrid>
      </Container>
    </Section>
  );
};

export default Funcionalidades;
