import React from 'react';
import styled from '@emotion/styled';
import { useContent } from '../../contexts/ContentContext';

const Container = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h3`
  color: #845EC2;
  margin-bottom: 20px;
  font-size: 18px;
`;

const EditableField = styled.div`
  margin-bottom: 20px;

  label {
    display: block;
    font-size: 14px;
    color: #666;
    margin-bottom: 8px;
  }

  input, textarea {
    width: 100%;
    padding: 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
    font-size: 14px;

    &:focus {
      outline: none;
      border-color: #FF6B6B;
    }
  }

  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const Button = styled.button`
  background: #FF6B6B;
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;
  transition: background-color 0.2s;

  &:hover {
    background: #ff5252;
  }
`;

const ParceirosContent: React.FC = () => {
  const { content, updateContent } = useContent();
  const [localContent, setLocalContent] = React.useState(content);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setLocalContent(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = () => {
    Object.entries(localContent).forEach(([key, value]) => {
      if (value !== content[key]) {
        updateContent(key, value);
      }
    });
  };

  return (
    <Container>
      <Section>
        <SectionTitle>Seção Principal</SectionTitle>
        <EditableField>
          <label>Título</label>
          <input
            type="text"
            name="partners.title"
            value={localContent['partners.title'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Subtítulo</label>
          <textarea
            name="partners.subtitle"
            value={localContent['partners.subtitle'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
      </Section>

      <Section>
        <SectionTitle>Estatísticas</SectionTitle>
        <EditableField>
          <label>Número de Usuárias</label>
          <input
            type="text"
            name="partners.stats.users"
            value={localContent['partners.stats.users'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Label de Usuárias</label>
          <input
            type="text"
            name="partners.stats.usersLabel"
            value={localContent['partners.stats.usersLabel'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Número de Parceiros</label>
          <input
            type="text"
            name="partners.stats.partners"
            value={localContent['partners.stats.partners'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Label de Parceiros</label>
          <input
            type="text"
            name="partners.stats.partnersLabel"
            value={localContent['partners.stats.partnersLabel'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Número de Cidades</label>
          <input
            type="text"
            name="partners.stats.cities"
            value={localContent['partners.stats.cities'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Label de Cidades</label>
          <input
            type="text"
            name="partners.stats.citiesLabel"
            value={localContent['partners.stats.citiesLabel'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
      </Section>

      <Section>
        <SectionTitle>Planos</SectionTitle>
        {['basic', 'pro', 'enterprise'].map((plan) => (
          <React.Fragment key={plan}>
            <EditableField>
              <label>{`Plano ${plan} - Título`}</label>
              <input
                type="text"
                name={`partners.plans.${plan}.title`}
                value={localContent[`partners.plans.${plan}.title`] || ''}
                onChange={handleInputChange}
              />
            </EditableField>
            <EditableField>
              <label>{`Plano ${plan} - Descrição`}</label>
              <textarea
                name={`partners.plans.${plan}.description`}
                value={localContent[`partners.plans.${plan}.description`] || ''}
                onChange={handleInputChange}
              />
            </EditableField>
            {[1, 2, 3, 4].map((featureIndex) => (
              <EditableField key={`${plan}-feature-${featureIndex}`}>
                <label>{`Plano ${plan} - Feature ${featureIndex}`}</label>
                <input
                  type="text"
                  name={`partners.plans.${plan}.feature${featureIndex}`}
                  value={localContent[`partners.plans.${plan}.feature${featureIndex}`] || ''}
                  onChange={handleInputChange}
                />
              </EditableField>
            ))}
          </React.Fragment>
        ))}
      </Section>

      <Button onClick={handleSave}>Salvar Alterações</Button>
    </Container>
  );
};

export default ParceirosContent;
