import React, { useState } from 'react';
import styled from '@emotion/styled';
import { useNavigate } from 'react-router-dom';
import DashboardEditor from './DashboardEditor';

const DashboardContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #FF6B6B 0%, #845EC2 100%);
  display: flex;
`;

const Sidebar = styled.div`
  width: 280px;
  background: rgba(255, 255, 255, 0.95);
  padding: 20px;
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const MainContent = styled.div`
  flex: 1;
  padding: 20px;
  overflow-y: auto;
`;

const Logo = styled.div`
  img {
    width: 180px;
    height: auto;
  }
`;

const PageList = styled.div`
  display: flex;
  flex-direction: column;
  gap: 10px;
`;

const PageItem = styled.div<{ active?: boolean }>`
  padding: 12px;
  background: ${props => props.active ? 'rgba(255, 107, 107, 0.1)' : 'transparent'};
  border-radius: 8px;
  cursor: pointer;
  transition: all 0.2s;

  &:hover {
    background: rgba(255, 107, 107, 0.05);
  }

  h3 {
    font-size: 16px;
    color: ${props => props.active ? '#FF6B6B' : '#333'};
    margin: 0 0 4px 0;
  }

  p {
    font-size: 12px;
    color: #666;
    margin: 0;
  }
`;

const pages = [
  {
    title: "Portal",
    description: "Página inicial do Portal Davida",
    path: "/portal"
  },
  {
    title: "Para Mulheres",
    description: "Seção dedicada às mulheres",
    path: "/para-mulheres"
  },
  {
    title: "Funcionalidades",
    description: "Recursos do aplicativo",
    path: "/funcionalidades"
  },
  {
    title: "Comunidade",
    description: "Seção de comunidade",
    path: "/comunidade"
  },
  {
    title: "Parceiros",
    description: "Parcerias e colaborações",
    path: "/parceiros"
  }
];

const Dashboard: React.FC = () => {
  const navigate = useNavigate();
  const [selectedPage, setSelectedPage] = useState(pages[0]);

  const handlePageSelect = (page: typeof pages[0]) => {
    setSelectedPage(page);
  };

  const handleSaveContent = async (content: any) => {
    try {
      // TODO: Implement actual API call to save content
      const response = await fetch(`/api/pages${selectedPage.path}`, {
        method: 'PUT',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(content),
      });

      if (!response.ok) {
        throw new Error('Failed to save content');
      }

      // Optionally refresh the page content or show a success message
    } catch (error) {
      console.error('Error saving content:', error);
      alert('Erro ao salvar o conteúdo. Por favor, tente novamente.');
    }
  };

  return (
    <DashboardContainer>
      <Sidebar>
        <Logo>
          <img src="/logo.png" alt="Davida Logo" />
        </Logo>
        <PageList>
          {pages.map((page) => (
            <PageItem
              key={page.path}
              active={selectedPage.path === page.path}
              onClick={() => handlePageSelect(page)}
            >
              <h3>{page.title}</h3>
              <p>{page.description}</p>
            </PageItem>
          ))}
        </PageList>
      </Sidebar>
      <MainContent>
        <DashboardEditor
          pagePath={selectedPage.path}
          onSave={handleSaveContent}
        />
      </MainContent>
    </DashboardContainer>
  );
};

export default Dashboard;
