interface PageContent {
  title: string;
  briefing: string;
  highlights: string;
}

export const pageContentService = {
  async getPageContent(pagePath: string): Promise<PageContent> {
    // Por enquanto, vamos usar localStorage para simular o armazenamento
    const storedContent = localStorage.getItem(`page_content_${pagePath}`);
    
    if (storedContent) {
      return JSON.parse(storedContent);
    }

    // Conteúdo padrão se não houver nada salvo
    return {
      title: 'Portal',
      briefing: 'Transformando vidas através da tecnologia e inovação...',
      highlights: 'Destaques importantes da página'
    };
  },

  async savePageContent(pagePath: string, content: PageContent): Promise<void> {
    // Salvar no localStorage
    localStorage.setItem(`page_content_${pagePath}`, JSON.stringify(content));
  }
};
