import React from 'react';
import styled from '@emotion/styled';
import { useContent } from '../../contexts/ContentContext';

const ContentContainer = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h2`
  color: #845EC2;
  margin-bottom: 20px;
  font-size: 20px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  
  label {
    display: block;
    margin-bottom: 8px;
    color: #333;
    font-weight: 500;
  }
  
  input, textarea {
    width: 100%;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 8px;
    font-size: 14px;
    
    &:focus {
      outline: none;
      border-color: #FF6B6B;
      box-shadow: 0 0 0 2px rgba(255, 107, 107, 0.1);
    }
  }
  
  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const SaveButton = styled.button`
  background: #FF6B6B;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background: #ff5252;
    transform: translateY(-1px);
  }
  
  &:active {
    transform: translateY(0);
  }
`;

const SuccessMessage = styled.div`
  background: #4CAF50;
  color: white;
  padding: 12px;
  border-radius: 8px;
  margin-top: 20px;
  text-align: center;
`;

const HomeContentEditor: React.FC = () => {
  const { content, updateContent } = useContent();
  const [showSuccess, setShowSuccess] = React.useState(false);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    updateContent(name, value);
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  return (
    <ContentContainer>
      <form onSubmit={handleSubmit}>
        <Section>
          <SectionTitle>Seção Hero</SectionTitle>
          <FormGroup>
            <label htmlFor="heroTitle">Título Principal</label>
            <input
              type="text"
              id="heroTitle"
              name="heroTitle"
              value={content.heroTitle}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label htmlFor="heroSubtitle">Subtítulo</label>
            <textarea
              id="heroSubtitle"
              name="heroSubtitle"
              value={content.heroSubtitle}
              onChange={handleChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Seção Sobre</SectionTitle>
          <FormGroup>
            <label htmlFor="aboutTitle">Título</label>
            <input
              type="text"
              id="aboutTitle"
              name="aboutTitle"
              value={content.aboutTitle}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label htmlFor="aboutText">Texto</label>
            <textarea
              id="aboutText"
              name="aboutText"
              value={content.aboutText}
              onChange={handleChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Seção Missão</SectionTitle>
          <FormGroup>
            <label htmlFor="missionTitle">Título</label>
            <input
              type="text"
              id="missionTitle"
              name="missionTitle"
              value={content.missionTitle}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label htmlFor="missionText">Texto</label>
            <textarea
              id="missionText"
              name="missionText"
              value={content.missionText}
              onChange={handleChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Seção Contato</SectionTitle>
          <FormGroup>
            <label htmlFor="contactTitle">Título</label>
            <input
              type="text"
              id="contactTitle"
              name="contactTitle"
              value={content.contactTitle}
              onChange={handleChange}
            />
          </FormGroup>
          <FormGroup>
            <label htmlFor="contactText">Texto</label>
            <textarea
              id="contactText"
              name="contactText"
              value={content.contactText}
              onChange={handleChange}
            />
          </FormGroup>
        </Section>

        <SaveButton type="submit">Salvar Alterações</SaveButton>

        {showSuccess && (
          <SuccessMessage>
            Conteúdo salvo com sucesso!
          </SuccessMessage>
        )}
      </form>
    </ContentContainer>
  );
};

export default HomeContentEditor;
