import React from 'react';
import styled from '@emotion/styled';
import { useContent } from '../../contexts/ContentContext';

interface Partner {
  name: string;
  description: string;
  logo: string;
  link: string;
}

const ContentContainer = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h2`
  color: #845EC2;
  margin-bottom: 20px;
  font-size: 20px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  
  label {
    display: block;
    margin-bottom: 8px;
    color: #333;
    font-weight: 500;
  }
  
  input, textarea {
    width: 100%;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 8px;
    font-size: 14px;
    
    &:focus {
      outline: none;
      border-color: #FF6B6B;
      box-shadow: 0 0 0 2px rgba(255, 107, 107, 0.1);
    }
  }
  
  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const PartnerItem = styled.div`
  background: #f8f9fa;
  padding: 20px;
  border-radius: 8px;
  margin-bottom: 20px;
`;

const SaveButton = styled.button`
  background: #FF6B6B;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background: #ff5252;
    transform: translateY(-1px);
  }
  
  &:active {
    transform: translateY(0);
  }
`;

const SuccessMessage = styled.div`
  background: #4CAF50;
  color: white;
  padding: 12px;
  border-radius: 8px;
  margin-top: 20px;
  text-align: center;
`;

const AddButton = styled.button`
  background: #6c757d;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 6px;
  font-size: 14px;
  cursor: pointer;
  margin-bottom: 20px;
  
  &:hover {
    background: #5a6268;
  }
`;

const RemoveButton = styled.button`
  background: #dc3545;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 6px;
  font-size: 14px;
  cursor: pointer;
  margin-top: 10px;
  
  &:hover {
    background: #c82333;
  }
`;

const ParceirosContentEditor: React.FC = () => {
  const { content, updatePageContent } = useContent();
  const [showSuccess, setShowSuccess] = React.useState(false);
  const pageContent = content.parceiros;

  const handleMainChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    updatePageContent('parceiros', { [name]: value });
  };

  const handlePartnerChange = (index: number, field: string, value: string) => {
    const newPartners = [...pageContent.partners];
    newPartners[index] = {
      ...newPartners[index],
      [field]: value
    };
    updatePageContent('parceiros', { partners: newPartners });
  };

  const handleBecomePartnerChange = (field: string, value: string) => {
    updatePageContent('parceiros', {
      becomePartner: {
        ...pageContent.becomePartner,
        [field]: value
      }
    });
  };

  const addPartner = () => {
    const newPartners = [...pageContent.partners, {
      name: 'Nome da Empresa',
      description: 'Descrição da empresa parceira',
      logo: '/images/partner-logo.jpg',
      link: 'https://empresa.com'
    }];
    updatePageContent('parceiros', { partners: newPartners });
  };

  const removePartner = (index: number) => {
    const newPartners = pageContent.partners.filter((_: Partner, i: number) => i !== index);
    updatePageContent('parceiros', { partners: newPartners });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  return (
    <ContentContainer>
      <form onSubmit={handleSubmit}>
        <Section>
          <SectionTitle>Conteúdo Principal</SectionTitle>
          <FormGroup>
            <label htmlFor="title">Título da Página</label>
            <input
              type="text"
              id="title"
              name="title"
              value={pageContent.title}
              onChange={handleMainChange}
            />
          </FormGroup>
          <FormGroup>
            <label htmlFor="subtitle">Subtítulo</label>
            <textarea
              id="subtitle"
              name="subtitle"
              value={pageContent.subtitle}
              onChange={handleMainChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Parceiros</SectionTitle>
          <AddButton type="button" onClick={addPartner}>
            + Adicionar Parceiro
          </AddButton>
          {pageContent.partners.map((partner: Partner, index: number) => (
            <PartnerItem key={index}>
              <FormGroup>
                <label>Nome da Empresa</label>
                <input
                  type="text"
                  value={partner.name}
                  onChange={(e) => handlePartnerChange(index, 'name', e.target.value)}
                />
              </FormGroup>
              <FormGroup>
                <label>Descrição</label>
                <textarea
                  value={partner.description}
                  onChange={(e) => handlePartnerChange(index, 'description', e.target.value)}
                />
              </FormGroup>
              <FormGroup>
                <label>URL do Logo</label>
                <input
                  type="text"
                  value={partner.logo}
                  onChange={(e) => handlePartnerChange(index, 'logo', e.target.value)}
                  placeholder="/images/logo.jpg"
                />
              </FormGroup>
              <FormGroup>
                <label>Link do Site</label>
                <input
                  type="text"
                  value={partner.link}
                  onChange={(e) => handlePartnerChange(index, 'link', e.target.value)}
                  placeholder="https://empresa.com"
                />
              </FormGroup>
              <RemoveButton type="button" onClick={() => removePartner(index)}>
                Remover Parceiro
              </RemoveButton>
            </PartnerItem>
          ))}
        </Section>

        <Section>
          <SectionTitle>Seção "Seja um Parceiro"</SectionTitle>
          <FormGroup>
            <label>Título</label>
            <input
              type="text"
              value={pageContent.becomePartner.title}
              onChange={(e) => handleBecomePartnerChange('title', e.target.value)}
            />
          </FormGroup>
          <FormGroup>
            <label>Texto</label>
            <textarea
              value={pageContent.becomePartner.text}
              onChange={(e) => handleBecomePartnerChange('text', e.target.value)}
            />
          </FormGroup>
        </Section>

        <SaveButton type="submit">Salvar Alterações</SaveButton>

        {showSuccess && (
          <SuccessMessage>
            Conteúdo salvo com sucesso!
          </SuccessMessage>
        )}
      </form>
    </ContentContainer>
  );
};

export default ParceirosContentEditor;
