import React, { useState } from 'react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';
import { theme } from '../../styles/theme';

const Section = styled.section`
  padding: ${theme.spacing['2xl']} ${theme.spacing.xl};
  padding-top: calc(${theme.spacing['2xl']} + 80px);
  background: linear-gradient(135deg, ${theme.colors.primary.peach} 0%, ${theme.colors.primary.rose} 100%);
  min-height: 100vh;
  overflow: hidden;
`;

const Container = styled.div`
  max-width: 1400px;
  margin: 0 auto;
`;

const CommunitySection = styled.div`
  display: grid;
  grid-template-columns: 1fr 1fr;
  gap: ${theme.spacing['2xl']};
  align-items: center;
  margin-bottom: ${theme.spacing['2xl']};

  @media (max-width: ${theme.breakpoints.lg}) {
    grid-template-columns: 1fr;
    gap: ${theme.spacing.xl};
  }
`;

const ContentColumn = styled.div`
  color: white;
`;

const Title = styled.h2`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize['4xl']};
  margin-bottom: ${theme.spacing.xl};
  color: white;
  text-align: center;
`;

const Subtitle = styled.p`
  font-size: ${theme.typography.fontSize.xl};
  margin-bottom: ${theme.spacing['2xl']};
  opacity: 0.9;
  line-height: 1.6;
  text-align: center;
  max-width: 800px;
  margin-left: auto;
  margin-right: auto;
`;

const TestimonialGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: ${theme.spacing.lg};

  @media (max-width: ${theme.breakpoints.md}) {
    grid-template-columns: 1fr;
  }
`;

const TestimonialCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.95);
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.xl};
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
`;

const TestimonialText = styled.p`
  color: ${theme.colors.text.primary};
  font-size: ${theme.typography.fontSize.lg};
  line-height: 1.6;
  margin-bottom: ${theme.spacing.md};
`;

const TestimonialAuthor = styled.div`
  display: flex;
  align-items: center;
  gap: ${theme.spacing.md};
`;

const AuthorAvatar = styled.div`
  width: 48px;
  height: 48px;
  border-radius: 50%;
  background: ${theme.colors.primary.coral};
  display: flex;
  align-items: center;
  justify-content: center;
  color: white;
  font-size: ${theme.typography.fontSize.lg};
`;

const AuthorInfo = styled.div`
  color: ${theme.colors.text.primary};
`;

const AuthorName = styled.p`
  font-weight: bold;
  margin-bottom: 4px;
`;

const AuthorRole = styled.p`
  font-size: ${theme.typography.fontSize.sm};
  opacity: 0.8;
`;

const AppPreviewColumn = styled.div`
  position: relative;
`;

const AppScreenshot = styled(motion.div)`
  background: white;
  border-radius: 55px;
  box-shadow: 0 20px 40px rgba(0, 0, 0, 0.2);
  overflow: hidden;
  position: relative;
  aspect-ratio: 9/19.5;
  max-width: 380px;
  margin: 0 auto;
  display: flex;
  flex-direction: column;
  background: linear-gradient(135deg, #FF8C7F 0%, #FF6B6B 100%);

  &::before {
    content: '';
    position: absolute;
    top: 12px;
    left: 50%;
    transform: translateX(-50%);
    width: 35%;
    height: 28px;
    background: #000;
    border-radius: 0 0 12px 12px;
    z-index: 2;
  }
`;

const PhoneHeader = styled.div`
  padding: 48px 20px 16px;
  display: flex;
  align-items: center;
  gap: 12px;
`;

const HeaderAvatar = styled.div`
  width: 36px;
  height: 36px;
  border-radius: 50%;
  background: white;
