import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';

export interface InvestidoresContent {
  hero: {
    title: string;
    subtitle: string;
  };
  sections: {
    icon: string;
    title: string;
    description: string;
    url: string;
  }[];
  stats: {
    value: string;
    label: string;
    icon: string;
  }[];
  ctaButton: {
    text: string;
    url: string;
  };
}

const defaultContent: InvestidoresContent = {
  hero: {
    title: "Invista no Futuro do Bem-estar Feminino",
    subtitle: "Seja parte de uma revolução no cuidado e apoio às mulheres através da tecnologia e espiritualidade"
  },
  sections: [
    {
      icon: "📈",
      title: "Oportunidade de Mercado",
      description: "O mercado de bem-estar feminino está em crescimento exponencial, com projeção de alcançar bilhões nos próximos anos",
      url: "/mercado"
    },
    {
      icon: "🎯",
      title: "Impacto Social",
      description: "Transformamos vidas através de tecnologia e espiritualidade, criando uma comunidade forte e engajada",
      url: "/impacto"
    },
    {
      icon: "💪",
      title: "Modelo de Negócio",
      description: "Estratégia sustentável combinando assinaturas premium, parcerias estratégicas e marketplace",
      url: "/modelo"
    }
  ],
  stats: [
    {
      value: "50mil+",
      label: "Usuárias Ativas",
      icon: "👥"
    },
    {
      value: "4.8/5",
      label: "Avaliação na App Store",
      icon: "⭐"
    },
    {
      value: "85%",
      label: "Taxa de Retenção",
      icon: "📊"
    }
  ],
  ctaButton: {
    text: "Seja um Investidor",
    url: "/contato-investidor"
  }
};

const EditorContainer = styled.div`
  padding: 30px;
  background: white;
  border-radius: 12px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  max-width: 1200px;
  margin: 40px auto;
`;

const Section = styled.div`
  margin-bottom: 40px;
  padding: 20px;
  background: #FFF5F5;
  border-radius: 8px;
`;

const SectionTitle = styled.h2`
  color: #333;
  margin-bottom: 20px;
  font-size: 24px;
  border-bottom: 2px solid #FF6B6B;
  padding-bottom: 10px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 8px;
  color: #666;
  font-weight: 500;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  margin-bottom: 10px;
  border: 1px solid #ddd;
  border-radius: 6px;
  font-size: 16px;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #845EC2;
    box-shadow: 0 0 0 2px rgba(132, 94, 194, 0.1);
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 12px;
  margin-bottom: 10px;
  border: 1px solid #ddd;
  border-radius: 6px;
  min-height: 120px;
  font-size: 16px;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #845EC2;
    box-shadow: 0 0 0 2px rgba(132, 94, 194, 0.1);
  }
`;

const Button = styled.button`
  background-color: #845EC2;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  font-size: 16px;
  font-weight: 500;
  transition: all 0.3s ease;

  &:hover {
    background-color: #6B4C9F;
    transform: translateY(-2px);
  }

  &:active {
    transform: translateY(0);
  }
`;

const InvestidoresContentEditor: React.FC = () => {
  const [content, setContent] = useState<InvestidoresContent>(defaultContent);
  const [showSuccess, setShowSuccess] = useState(false);

  useEffect(() => {
    const savedContent = localStorage.getItem('investidoresContent');
    if (savedContent) {
      setContent(JSON.parse(savedContent));
    }
  }, []);

  const handleSave = () => {
    localStorage.setItem('investidoresContent', JSON.stringify(content));
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  const updateHero = (field: keyof typeof content.hero, value: string) => {
    setContent(prev => ({
      ...prev,
      hero: { ...prev.hero, [field]: value }
    }));
  };

  const updateSection = (index: number, field: keyof typeof content.sections[0], value: string) => {
    setContent(prev => ({
      ...prev,
      sections: prev.sections.map((section, i) => 
        i === index ? { ...section, [field]: value } : section
      )
    }));
  };

  const updateStat = (index: number, field: keyof typeof content.stats[0], value: string) => {
    setContent(prev => ({
      ...prev,
      stats: prev.stats.map((stat, i) => 
        i === index ? { ...stat, [field]: value } : stat
      )
    }));
  };

  const updateCTA = (field: keyof typeof content.ctaButton, value: string) => {
    setContent(prev => ({
      ...prev,
      ctaButton: { ...prev.ctaButton, [field]: value }
    }));
  };

  return (
    <EditorContainer>
      <Section>
        <SectionTitle>Seção Hero</SectionTitle>
        <FormGroup>
          <Label>Título Principal</Label>
          <Input
            value={content.hero.title}
            onChange={(e) => updateHero('title', e.target.value)}
            placeholder="Digite o título principal"
          />
        </FormGroup>
        <FormGroup>
          <Label>Subtítulo</Label>
          <TextArea
            value={content.hero.subtitle}
            onChange={(e) => updateHero('subtitle', e.target.value)}
            placeholder="Digite o subtítulo"
          />
        </FormGroup>
      </Section>

      {content.sections.map((section, index) => (
        <Section key={index}>
          <SectionTitle>{section.title}</SectionTitle>
          <FormGroup>
            <Label>Ícone</Label>
            <Input
              value={section.icon}
              onChange={(e) => updateSection(index, 'icon', e.target.value)}
              placeholder="Digite o ícone (emoji)"
            />
          </FormGroup>
          <FormGroup>
            <Label>Título da Seção</Label>
            <Input
              value={section.title}
              onChange={(e) => updateSection(index, 'title', e.target.value)}
              placeholder="Digite o título da seção"
            />
          </FormGroup>
          <FormGroup>
            <Label>Descrição</Label>
            <TextArea
              value={section.description}
              onChange={(e) => updateSection(index, 'description', e.target.value)}
              placeholder="Digite a descrição da seção"
            />
          </FormGroup>
          <FormGroup>
            <Label>URL</Label>
            <Input
              value={section.url}
              onChange={(e) => updateSection(index, 'url', e.target.value)}
              placeholder="Digite a URL da seção"
            />
          </FormGroup>
        </Section>
      ))}

      <Section>
        <SectionTitle>Estatísticas</SectionTitle>
        {content.stats.map((stat, index) => (
          <div key={index} style={{ marginBottom: '20px' }}>
            <FormGroup>
              <Label>Ícone</Label>
              <Input
                value={stat.icon}
                onChange={(e) => updateStat(index, 'icon', e.target.value)}
                placeholder="Digite o ícone (emoji)"
              />
            </FormGroup>
            <FormGroup>
              <Label>Valor</Label>
              <Input
                value={stat.value}
                onChange={(e) => updateStat(index, 'value', e.target.value)}
                placeholder="Digite o valor"
              />
            </FormGroup>
            <FormGroup>
              <Label>Rótulo</Label>
              <Input
                value={stat.label}
                onChange={(e) => updateStat(index, 'label', e.target.value)}
                placeholder="Digite o rótulo"
              />
            </FormGroup>
          </div>
        ))}
      </Section>

      <Section>
        <SectionTitle>Botão CTA</SectionTitle>
        <FormGroup>
          <Label>Texto do Botão</Label>
          <Input
            value={content.ctaButton.text}
            onChange={(e) => updateCTA('text', e.target.value)}
            placeholder="Digite o texto do botão"
          />
        </FormGroup>
        <FormGroup>
          <Label>URL do Botão</Label>
          <Input
            value={content.ctaButton.url}
            onChange={(e) => updateCTA('url', e.target.value)}
            placeholder="Digite a URL do botão"
          />
        </FormGroup>
      </Section>

      <Button onClick={handleSave}>Salvar Alterações</Button>
      {showSuccess && (
        <div style={{ 
          marginTop: '20px', 
          padding: '10px', 
          background: '#4CAF50', 
          color: 'white', 
          borderRadius: '6px',
          textAlign: 'center' 
        }}>
          Conteúdo salvo com sucesso!
        </div>
      )}
    </EditorContainer>
  );
};

export default InvestidoresContentEditor;
