import React from 'react';
import styled from '@emotion/styled';
import { useContent } from '../../contexts/ContentContext';

const Container = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h3`
  color: #845EC2;
  margin-bottom: 20px;
  font-size: 18px;
`;

const EditableField = styled.div`
  margin-bottom: 20px;

  label {
    display: block;
    font-size: 14px;
    color: #666;
    margin-bottom: 8px;
  }

  input, textarea {
    width: 100%;
    padding: 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
    font-size: 14px;

    &:focus {
      outline: none;
      border-color: #FF6B6B;
    }
  }

  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const Button = styled.button`
  background: #FF6B6B;
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;
  transition: background-color 0.2s;

  &:hover {
    background: #ff5252;
  }
`;

const ContatoContent: React.FC = () => {
  const { content, updateContent } = useContent();
  const [localContent, setLocalContent] = React.useState(content);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setLocalContent(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = () => {
    Object.entries(localContent).forEach(([key, value]) => {
      if (value !== content[key]) {
        updateContent(key, value);
      }
    });
  };

  return (
    <Container>
      <Section>
        <SectionTitle>Seção Principal</SectionTitle>
        <EditableField>
          <label>Título</label>
          <input
            type="text"
            name="contact.title"
            value={localContent['contact.title'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Subtítulo</label>
          <textarea
            name="contact.subtitle"
            value={localContent['contact.subtitle'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
      </Section>

      <Section>
        <SectionTitle>Informações de Contato</SectionTitle>
        <EditableField>
          <label>Título da Seção</label>
          <input
            type="text"
            name="contact.info.title"
            value={localContent['contact.info.title'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>E-mail</label>
          <input
            type="text"
            name="contact.info.email"
            value={localContent['contact.info.email'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Telefone</label>
          <input
            type="text"
            name="contact.info.phone"
            value={localContent['contact.info.phone'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Endereço</label>
          <input
            type="text"
            name="contact.info.address"
            value={localContent['contact.info.address'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
      </Section>

      <Section>
        <SectionTitle>Redes Sociais</SectionTitle>
        <EditableField>
          <label>Link do Facebook</label>
          <input
            type="text"
            name="contact.social.facebook"
            value={localContent['contact.social.facebook'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Link do Instagram</label>
          <input
            type="text"
            name="contact.social.instagram"
            value={localContent['contact.social.instagram'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
      </Section>

      <Section>
        <SectionTitle>Formulário de Contato</SectionTitle>
        <EditableField>
          <label>Título do Formulário</label>
          <input
            type="text"
            name="contact.form.title"
            value={localContent['contact.form.title'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Label do Campo Nome</label>
          <input
            type="text"
            name="contact.form.nameLabel"
            value={localContent['contact.form.nameLabel'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Label do Campo Email</label>
          <input
            type="text"
            name="contact.form.emailLabel"
            value={localContent['contact.form.emailLabel'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Label do Campo Assunto</label>
          <input
            type="text"
            name="contact.form.subjectLabel"
            value={localContent['contact.form.subjectLabel'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Label do Campo Mensagem</label>
          <input
            type="text"
            name="contact.form.messageLabel"
            value={localContent['contact.form.messageLabel'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Texto do Botão de Envio</label>
          <input
            type="text"
            name="contact.form.submitButton"
            value={localContent['contact.form.submitButton'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
      </Section>

      <Section>
        <SectionTitle>Perguntas Frequentes</SectionTitle>
        <EditableField>
          <label>Título da Seção</label>
          <input
            type="text"
            name="contact.faq.title"
            value={localContent['contact.faq.title'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        {[1, 2, 3, 4].map((index) => (
          <React.Fragment key={index}>
            <EditableField>
              <label>{`Pergunta ${index}`}</label>
              <input
                type="text"
                name={`contact.faq.q${index}`}
                value={localContent[`contact.faq.q${index}`] || ''}
                onChange={handleInputChange}
              />
            </EditableField>
            <EditableField>
              <label>{`Resposta ${index}`}</label>
              <textarea
                name={`contact.faq.a${index}`}
                value={localContent[`contact.faq.a${index}`] || ''}
                onChange={handleInputChange}
              />
            </EditableField>
          </React.Fragment>
        ))}
      </Section>

      <Button onClick={handleSave}>Salvar Alterações</Button>
    </Container>
  );
};

export default ContatoContent;
