import React, { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import styled from '@emotion/styled';
import { theme } from '../../styles/theme';
import { useContent } from '../../contexts/ContentContext';

const Container = styled.div`
  min-height: 100vh;
  padding: ${theme.spacing['2xl']};
  background-color: ${theme.colors.secondary.cream};
`;

const LoginContainer = styled.div`
  max-width: 400px;
  margin: 0 auto;
  padding: ${theme.spacing.xl};
  background-color: ${theme.colors.secondary.white};
  border-radius: ${theme.borderRadius.lg};
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Title = styled.h1`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize['3xl']};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.xl};
  text-align: center;
`;

const Input = styled.input`
  width: 100%;
  padding: ${theme.spacing.md};
  margin-bottom: ${theme.spacing.lg};
  border: 1px solid ${theme.colors.text.secondary};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.base};
`;

const Button = styled.button`
  width: 100%;
  padding: ${theme.spacing.md};
  background-color: ${theme.colors.primary.coral};
  color: ${theme.colors.secondary.white};
  border: none;
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.base};
  font-weight: 600;
  cursor: pointer;
  transition: background-color 0.2s;

  &:hover {
    background-color: ${theme.colors.primary.peach};
  }
`;

const ContentEditor = styled.div`
  max-width: 800px;
  margin: 0 auto;
`;

const ContentSection = styled.div`
  margin-bottom: ${theme.spacing.xl};
`;

const Label = styled.label`
  display: block;
  font-size: ${theme.typography.fontSize.lg};
  font-weight: 600;
  margin-bottom: ${theme.spacing.sm};
`;

const Textarea = styled.textarea`
  width: 100%;
  padding: ${theme.spacing.md};
  margin-bottom: ${theme.spacing.lg};
  border: 1px solid ${theme.colors.text.secondary};
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.base};
  min-height: 100px;
`;

export const Admin: React.FC = () => {
  const [isLoggedIn, setIsLoggedIn] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const { content, updateContent } = useContent();
  const navigate = useNavigate();

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (username === 'admin' && password === 'admin') {
      setIsLoggedIn(true);
    } else {
      alert('Credenciais inválidas');
    }
  };

  const handleLogout = () => {
    setIsLoggedIn(false);
    setUsername('');
    setPassword('');
    navigate('/');
  };

  if (!isLoggedIn) {
    return (
      <Container>
        <LoginContainer>
          <Title>Login Admin</Title>
          <form onSubmit={handleLogin}>
            <Input
              type="text"
              placeholder="Usuário"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <Input
              type="password"
              placeholder="Senha"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button type="submit">Entrar</Button>
          </form>
        </LoginContainer>
      </Container>
    );
  }

  return (
    <Container>
      <ContentEditor>
        <Title>Editor de Conteúdo</Title>
        <ContentSection>
          <Label>Título do Hero</Label>
          <Input
            value={content.heroTitle}
            onChange={(e) => updateContent('heroTitle', e.target.value)}
          />
        </ContentSection>
        <ContentSection>
          <Label>Subtítulo do Hero</Label>
          <Input
            value={content.heroSubtitle}
            onChange={(e) => updateContent('heroSubtitle', e.target.value)}
          />
        </ContentSection>
        <ContentSection>
          <Label>Título Sobre</Label>
          <Input
            value={content.aboutTitle}
            onChange={(e) => updateContent('aboutTitle', e.target.value)}
          />
        </ContentSection>
        <ContentSection>
          <Label>Texto Sobre</Label>
          <Textarea
            value={content.aboutText}
            onChange={(e) => updateContent('aboutText', e.target.value)}
          />
        </ContentSection>
        <ContentSection>
          <Label>Título Missão</Label>
          <Input
            value={content.missionTitle}
            onChange={(e) => updateContent('missionTitle', e.target.value)}
          />
        </ContentSection>
        <ContentSection>
          <Label>Texto Missão</Label>
          <Textarea
            value={content.missionText}
            onChange={(e) => updateContent('missionText', e.target.value)}
          />
        </ContentSection>
        <ContentSection>
          <Label>Título Contato</Label>
          <Input
            value={content.contactTitle}
            onChange={(e) => updateContent('contactTitle', e.target.value)}
          />
        </ContentSection>
        <ContentSection>
          <Label>Texto Contato</Label>
          <Textarea
            value={content.contactText}
            onChange={(e) => updateContent('contactText', e.target.value)}
          />
        </ContentSection>
        <Button onClick={handleLogout}>Sair</Button>
      </ContentEditor>
    </Container>
  );
};
