import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';

const ContentContainer = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h2`
  color: #845EC2;
  margin-bottom: 20px;
  font-size: 20px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  
  label {
    display: block;
    margin-bottom: 8px;
    color: #333;
    font-weight: 500;
  }
  
  input, textarea {
    width: 100%;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 8px;
    font-size: 14px;
    
    &:focus {
      outline: none;
      border-color: #FF6B6B;
      box-shadow: 0 0 0 2px rgba(255, 107, 107, 0.1);
    }
  }
  
  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const FeatureSection = styled.div`
  background: #f8f9fa;
  padding: 20px;
  border-radius: 8px;
  margin-bottom: 20px;
`;

const SaveButton = styled.button`
  background: #FF6B6B;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background: #ff5252;
    transform: translateY(-1px);
  }
  
  &:active {
    transform: translateY(0);
  }
`;

const SuccessMessage = styled.div`
  background: #4CAF50;
  color: white;
  padding: 12px;
  border-radius: 8px;
  margin-top: 20px;
  text-align: center;
`;

interface Feature {
  title: string;
  description: string;
}

interface DownloadAppContent {
  title: string;
  description: string;
  playStoreLink: string;
  appStoreLink: string;
  previewImage1: string;
  previewImage2: string;
  features: Feature[];
}

const defaultContent: DownloadAppContent = {
  title: "Baixe o App Davida",
  description: "Tenha acesso à sua jornada espiritual na palma da sua mão. Conecte-se com sua comunidade, acesse conteúdos exclusivos e acompanhe seu desenvolvimento pessoal em qualquer lugar.",
  playStoreLink: "https://play.google.com/store",
  appStoreLink: "https://apps.apple.com",
  previewImage1: "/app-preview-1.png",
  previewImage2: "/app-preview-2.png",
  features: [
    {
      title: "Meditação Guiada",
      description: "Acesse uma biblioteca completa de meditações guiadas para diferentes momentos e necessidades."
    },
    {
      title: "Comunidade",
      description: "Conecte-se com pessoas que compartilham dos mesmos interesses e valores espirituais."
    },
    {
      title: "Conteúdo Exclusivo",
      description: "Tenha acesso a artigos, vídeos e áudios exclusivos para seu desenvolvimento espiritual."
    },
    {
      title: "Jornada Personalizada",
      description: "Acompanhe seu progresso e receba recomendações personalizadas para sua jornada."
    }
  ]
};

const DownloadAppEditor: React.FC = () => {
  const [content, setContent] = useState<DownloadAppContent>(defaultContent);
  const [showSuccess, setShowSuccess] = useState(false);

  useEffect(() => {
    const savedContent = localStorage.getItem('downloadAppContent');
    if (savedContent) {
      setContent(JSON.parse(savedContent));
    }
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setContent(prev => ({
      ...prev,
      [name]: value
    }));
  };

  const handleFeatureChange = (index: number, field: 'title' | 'description', value: string) => {
    setContent(prev => ({
      ...prev,
      features: prev.features.map((feature, i) => 
        i === index ? { ...feature, [field]: value } : feature
      )
    }));
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    localStorage.setItem('downloadAppContent', JSON.stringify(content));
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  return (
    <ContentContainer>
      <form onSubmit={handleSubmit}>
        <Section>
          <SectionTitle>Conteúdo Principal</SectionTitle>
          
          <FormGroup>
            <label htmlFor="title">Título da Página</label>
            <input
              type="text"
              id="title"
              name="title"
              value={content.title}
              onChange={handleChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="description">Descrição Principal</label>
            <textarea
              id="description"
              name="description"
              value={content.description}
              onChange={handleChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Links das Lojas</SectionTitle>
          
          <FormGroup>
            <label htmlFor="playStoreLink">Link do Google Play</label>
            <input
              type="text"
              id="playStoreLink"
              name="playStoreLink"
              value={content.playStoreLink}
              onChange={handleChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="appStoreLink">Link da App Store</label>
            <input
              type="text"
              id="appStoreLink"
              name="appStoreLink"
              value={content.appStoreLink}
              onChange={handleChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Imagens de Preview</SectionTitle>
          
          <FormGroup>
            <label htmlFor="previewImage1">URL da Imagem de Preview 1</label>
            <input
              type="text"
              id="previewImage1"
              name="previewImage1"
              value={content.previewImage1}
              onChange={handleChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="previewImage2">URL da Imagem de Preview 2</label>
            <input
              type="text"
              id="previewImage2"
              name="previewImage2"
              value={content.previewImage2}
              onChange={handleChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Funcionalidades</SectionTitle>
          
          {content.features.map((feature, index) => (
            <FeatureSection key={index}>
              <FormGroup>
                <label htmlFor={`feature-${index}-title`}>Título da Funcionalidade {index + 1}</label>
                <input
                  type="text"
                  id={`feature-${index}-title`}
                  value={feature.title}
                  onChange={(e) => handleFeatureChange(index, 'title', e.target.value)}
                />
              </FormGroup>

              <FormGroup>
                <label htmlFor={`feature-${index}-description`}>Descrição da Funcionalidade {index + 1}</label>
                <textarea
                  id={`feature-${index}-description`}
                  value={feature.description}
                  onChange={(e) => handleFeatureChange(index, 'description', e.target.value)}
                />
              </FormGroup>
            </FeatureSection>
          ))}
        </Section>

        <SaveButton type="submit">Salvar Alterações</SaveButton>

        {showSuccess && (
          <SuccessMessage>
            Conteúdo salvo com sucesso!
          </SuccessMessage>
        )}
      </form>
    </ContentContainer>
  );
};

export default DownloadAppEditor;
