import React, { useState } from 'react';
import styled from '@emotion/styled';
import { useNavigate } from 'react-router-dom';

const PageContainer = styled.div`
  min-height: 100vh;
  background: linear-gradient(135deg, #FF6B6B 0%, #FFE66D 100%);
  display: flex;
  padding: 40px;
`;

const LeftSection = styled.div`
  flex: 1;
  padding: 40px;
  display: flex;
  flex-direction: column;
  justify-content: center;
  color: white;
`;

const RightSection = styled.div`
  width: 400px;
  padding: 40px;
  display: flex;
  align-items: center;
`;

const LoginContainer = styled.div`
  background: white;
  padding: 40px;
  border-radius: 10px;
  box-shadow: 0 4px 20px rgba(0,0,0,0.1);
  width: 100%;
`;

const Logo = styled.div`
  margin-bottom: 40px;
  img {
    width: 200px;
    height: auto;
  }
`;

const Briefing = styled.div`
  margin-bottom: 40px;
  
  h2 {
    font-size: 32px;
    margin-bottom: 20px;
    font-weight: 600;
  }
  
  p {
    font-size: 18px;
    line-height: 1.6;
    margin-bottom: 20px;
    opacity: 0.9;
  }
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  margin: 10px 0;
  border: 1px solid #ddd;
  border-radius: 6px;
  font-size: 16px;
  transition: border-color 0.2s;

  &:focus {
    border-color: #FF6B6B;
    outline: none;
  }
`;

const Button = styled.button`
  width: 100%;
  padding: 14px;
  background-color: #FF6B6B;
  color: white;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  font-size: 16px;
  font-weight: 600;
  margin-top: 20px;
  transition: background-color 0.2s;

  &:hover {
    background-color: #ff5252;
  }
`;

const InvestorsButton = styled(Button)`
  background-color: #4CAF50;
  margin-top: 30px;
  width: auto;
  padding: 14px 28px;

  &:hover {
    background-color: #45a049;
  }
`;

const Title = styled.h1`
  color: #333;
  text-align: center;
  margin-bottom: 30px;
  font-size: 24px;
  font-weight: 600;
`;

const Admin = () => {
  const [isAuthenticated, setIsAuthenticated] = useState(false);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (username === 'admin' && password === 'admin') {
      setIsAuthenticated(true);
    } else {
      alert('UsuÃ¡rio ou senha incorretos');
    }
  };

  const handleLogout = () => {
    setIsAuthenticated(false);
    setUsername('');
    setPassword('');
  };

  if (!isAuthenticated) {
    return (
      <PageContainer>
        <LeftSection>
          <Logo>
            <img src="/logo-davida.png" alt="Davida Logo" />
          </Logo>
          <Briefing>
            <h2>Bem-vinda Ã  Davida</h2>
            <p>
              A Davida Ã© uma plataforma inovadora dedicada ao empoderamento feminino,
              conectando mulheres e proporcionando um ambiente seguro para crescimento
              pessoal e profissional.
            </p>
            <p>
              Nossa missÃ£o Ã© transformar vidas atravÃ©s da tecnologia, educaÃ§Ã£o e
              networking, criando oportunidades reais para mulheres em todo o Brasil.
            </p>
            <InvestorsButton onClick={() => navigate('/investidores')}>
              Ãrea de Investidores
            </InvestorsButton>
          </Briefing>
        </LeftSection>
        <RightSection>
          <LoginContainer>
            <Title>Acesso Administrativo</Title>
            <form onSubmit={handleLogin}>
              <Input
                type="text"
                placeholder="UsuÃ¡rio"
                value={username}
                onChange={(e) => setUsername(e.target.value)}
              />
              <Input
                type="password"
                placeholder="Senha"
                value={password}
                onChange={(e) => setPassword(e.target.value)}
              />
              <Button type="submit">Entrar</Button>
            </form>
          </LoginContainer>
        </RightSection>
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <LeftSection>
        <Logo>
          <img src="/logo-davida.png" alt="Davida Logo" />
        </Logo>
        <Briefing>
          <h2>Painel Administrativo</h2>
          <p>
            Bem-vinda ao painel administrativo da Davida. Aqui vocÃª pode gerenciar
            o conteÃºdo da plataforma e acompanhar as principais mÃ©tricas.
          </p>
          <InvestorsButton onClick={() => navigate('/investidores')}>
            Ãrea de Investidores
          </InvestorsButton>
        </Briefing>
        <Button onClick={handleLogout}>Sair</Button>
      </LeftSection>
    </PageContainer>
  );
};

export default Admin;
