import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';

const ContentContainer = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h2`
  color: #845EC2;
  margin-bottom: 20px;
  font-size: 20px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  
  label {
    display: block;
    margin-bottom: 8px;
    color: #333;
    font-weight: 500;
  }
  
  input, textarea {
    width: 100%;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 8px;
    font-size: 14px;
    
    &:focus {
      outline: none;
      border-color: #FF6B6B;
      box-shadow: 0 0 0 2px rgba(255, 107, 107, 0.1);
    }
  }
  
  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const SaveButton = styled.button`
  background: #FF6B6B;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background: #ff5252;
    transform: translateY(-1px);
  }
  
  &:active {
    transform: translateY(0);
  }
`;

const SuccessMessage = styled.div`
  background: #4CAF50;
  color: white;
  padding: 12px;
  border-radius: 8px;
  margin-top: 20px;
  text-align: center;
`;

const IconPreview = styled.div`
  width: 100px;
  height: 100px;
  border: 2px dashed #ddd;
  border-radius: 8px;
  margin-top: 10px;
  display: flex;
  align-items: center;
  justify-content: center;
  overflow: hidden;
  
  img {
    max-width: 100%;
    max-height: 100%;
    object-fit: contain;
  }
`;

export interface ParaMulheresContent {
  heroTitle: string;
  heroSubtitle: string;
  sections: {
    title: string;
    description: string;
    iconUrl: string;
  }[];
  features: {
    title: string;
    description: string;
    iconUrl: string;
  }[];
  ctaTitle: string;
  ctaText: string;
  ctaButtonText: string;
  ctaButtonLink: string;
}

const defaultContent: ParaMulheresContent = {
  heroTitle: "Um espaço dedicado ao seu bem-estar",
  heroSubtitle: "Encontre apoio, conexão e crescimento em sua jornada",
  sections: [
    {
      title: "Acolhimento",
      description: "Um ambiente seguro para compartilhar experiências e encontrar apoio em sua jornada",
      iconUrl: "/images/icons/acolhimento.svg"
    },
    {
      title: "Espiritualidade",
      description: "Fortaleça sua conexão espiritual através de práticas e reflexões guiadas",
      iconUrl: "/images/icons/espiritualidade.svg"
    },
    {
      title: "Saúde Emocional",
      description: "Cuide do seu bem-estar emocional com ferramentas e suporte especializado",
      iconUrl: "/images/icons/saude-emocional.svg"
    }
  ],
  features: [
    {
      title: "Grupos de Apoio",
      description: "Conecte-se com outras mulheres que compartilham experiências similares",
      iconUrl: "/images/icons/grupos.svg"
    },
    {
      title: "Conteúdo Exclusivo",
      description: "Acesse materiais e recursos desenvolvidos especialmente para você",
      iconUrl: "/images/icons/conteudo.svg"
    },
    {
      title: "Mentoria",
      description: "Receba orientação personalizada de especialistas em diferentes áreas",
      iconUrl: "/images/icons/mentoria.svg"
    }
  ],
  ctaTitle: "Comece Sua Jornada",
  ctaText: "Junte-se a milhares de mulheres que já encontraram apoio e crescimento no Davida",
  ctaButtonText: "Cadastre-se Agora",
  ctaButtonLink: "/cadastro"
};

const ParaMulheresContentEditor: React.FC = () => {
  const [content, setContent] = useState<ParaMulheresContent>(defaultContent);
  const [showSuccess, setShowSuccess] = useState(false);

  useEffect(() => {
    const savedContent = localStorage.getItem('paraMulheresContent');
    if (savedContent) {
      setContent(JSON.parse(savedContent));
    }
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    
    if (name.includes('.')) {
      const [section, index, field] = name.split('.');
      setContent(prev => {
        const newContent = { ...prev };
        if (section === 'sections') {
          newContent.sections = [...prev.sections];
          newContent.sections[Number(index)] = {
            ...newContent.sections[Number(index)],
            [field]: value
          };
        } else if (section === 'features') {
          newContent.features = [...prev.features];
          newContent.features[Number(index)] = {
            ...newContent.features[Number(index)],
            [field]: value
          };
        }
        return newContent;
      });
    } else {
      setContent(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    localStorage.setItem('paraMulheresContent', JSON.stringify(content));
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  return (
    <ContentContainer>
      <form onSubmit={handleSubmit}>
        <Section>
          <SectionTitle>Seção Hero</SectionTitle>
          <FormGroup>
            <label htmlFor="heroTitle">Título Principal</label>
            <textarea
              id="heroTitle"
              name="heroTitle"
              value={content.heroTitle}
              onChange={handleChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="heroSubtitle">Subtítulo</label>
            <textarea
              id="heroSubtitle"
              name="heroSubtitle"
              value={content.heroSubtitle}
              onChange={handleChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Seções Principais</SectionTitle>
          {content.sections.map((section, index) => (
            <div key={index} style={{ marginBottom: '30px', paddingBottom: '20px', borderBottom: '1px solid #eee' }}>
              <FormGroup>
                <label htmlFor={`sections.${index}.title`}>Título da Seção {index + 1}</label>
                <input
                  type="text"
                  id={`sections.${index}.title`}
                  name={`sections.${index}.title`}
                  value={section.title}
                  onChange={handleChange}
                />
              </FormGroup>

              <FormGroup>
                <label htmlFor={`sections.${index}.description`}>Descrição da Seção {index + 1}</label>
                <textarea
                  id={`sections.${index}.description`}
                  name={`sections.${index}.description`}
                  value={section.description}
                  onChange={handleChange}
                />
              </FormGroup>

              <FormGroup>
                <label htmlFor={`sections.${index}.iconUrl`}>URL do Ícone {index + 1}</label>
                <input
                  type="text"
                  id={`sections.${index}.iconUrl`}
                  name={`sections.${index}.iconUrl`}
                  value={section.iconUrl}
                  onChange={handleChange}
                />
                <IconPreview>
                  {section.iconUrl && <img src={section.iconUrl} alt={`Ícone ${section.title}`} />}
                </IconPreview>
              </FormGroup>
            </div>
          ))}
        </Section>

        <Section>
          <SectionTitle>Recursos</SectionTitle>
          {content.features.map((feature, index) => (
            <div key={index} style={{ marginBottom: '30px', paddingBottom: '20px', borderBottom: '1px solid #eee' }}>
              <FormGroup>
                <label htmlFor={`features.${index}.title`}>Título do Recurso {index + 1}</label>
                <input
                  type="text"
                  id={`features.${index}.title`}
                  name={`features.${index}.title`}
                  value={feature.title}
                  onChange={handleChange}
                />
              </FormGroup>

              <FormGroup>
                <label htmlFor={`features.${index}.description`}>Descrição do Recurso {index + 1}</label>
                <textarea
                  id={`features.${index}.description`}
                  name={`features.${index}.description`}
                  value={feature.description}
                  onChange={handleChange}
                />
              </FormGroup>

              <FormGroup>
                <label htmlFor={`features.${index}.iconUrl`}>URL do Ícone {index + 1}</label>
                <input
                  type="text"
                  id={`features.${index}.iconUrl`}
                  name={`features.${index}.iconUrl`}
                  value={feature.iconUrl}
                  onChange={handleChange}
                />
                <IconPreview>
                  {feature.iconUrl && <img src={feature.iconUrl} alt={`Ícone ${feature.title}`} />}
                </IconPreview>
              </FormGroup>
            </div>
          ))}
        </Section>

        <Section>
          <SectionTitle>Seção CTA</SectionTitle>
          <FormGroup>
            <label htmlFor="ctaTitle">Título do CTA</label>
            <input
              type="text"
              id="ctaTitle"
              name="ctaTitle"
              value={content.ctaTitle}
              onChange={handleChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="ctaText">Texto do CTA</label>
            <textarea
              id="ctaText"
              name="ctaText"
              value={content.ctaText}
              onChange={handleChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="ctaButtonText">Texto do Botão</label>
            <input
              type="text"
              id="ctaButtonText"
              name="ctaButtonText"
              value={content.ctaButtonText}
              onChange={handleChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="ctaButtonLink">Link do Botão</label>
            <input
              type="text"
              id="ctaButtonLink"
              name="ctaButtonLink"
              value={content.ctaButtonLink}
              onChange={handleChange}
            />
          </FormGroup>
        </Section>

        <SaveButton type="submit">Salvar Alterações</SaveButton>

        {showSuccess && (
          <SuccessMessage>
            Conteúdo salvo com sucesso!
          </SuccessMessage>
        )}
      </form>
    </ContentContainer>
  );
};

export default ParaMulheresContentEditor;
