import React from 'react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet-async';
import { theme } from '../../styles/theme';

const Section = styled.section`
  padding: ${theme.spacing['2xl']} ${theme.spacing.xl};
  background: linear-gradient(135deg, #FF6B6B 0%, #845EC2 100%);
  min-height: 100vh;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding-top: ${theme.spacing['2xl']};
`;

const Title = styled.h1`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize['4xl']};
  color: white;
  text-align: center;
  margin-bottom: ${theme.spacing.md};
`;

const Subtitle = styled.h2`
  font-size: ${theme.typography.fontSize.xl};
  color: rgba(255, 255, 255, 0.9);
  text-align: center;
  max-width: 800px;
  margin: 0 auto ${theme.spacing.xl};
  line-height: 1.6;
`;

const Card = styled(motion.div)`
  background: rgba(255, 255, 255, 0.95);
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.xl};
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
  margin-bottom: ${theme.spacing.xl};
`;

const Grid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
  gap: ${theme.spacing.xl};
  margin: ${theme.spacing['2xl']} 0;
`;

const WhyInvestCard = styled(Card)`
  text-align: left;
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.md};
`;

const WhyInvestTitle = styled.h3`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize['2xl']};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.sm};
`;

const WhyInvestText = styled.p`
  font-size: ${theme.typography.fontSize.base};
  color: ${theme.colors.text.secondary};
  line-height: 1.6;
`;

const Table = styled.table`
  width: 100%;
  border-collapse: collapse;
  margin: ${theme.spacing.xl} 0;
`;

const Th = styled.th`
  text-align: left;
  padding: ${theme.spacing.md};
  background: ${theme.colors.primary.coral};
  color: white;
  font-weight: bold;
`;

const Td = styled.td`
  padding: ${theme.spacing.md};
  border-bottom: 1px solid rgba(0, 0, 0, 0.1);
`;

const SponsorshipCard = styled(Card)`
  h3 {
    color: ${theme.colors.primary.coral};
    margin-bottom: ${theme.spacing.md};
  }
`;

const List = styled.ul`
    list-style: none;
    padding: 0;
  margin: ${theme.spacing.md} 0;

    li {
      margin-bottom: ${theme.spacing.sm};
    display: flex;
    align-items: center;
    gap: ${theme.spacing.sm};

    &:before {
      content: "â€¢";
      color: ${theme.colors.primary.coral};
    font-weight: bold;
    }
  }
`;

const MarketingSection = styled(Card)`
  h3 {
  color: ${theme.colors.primary.coral};
    margin-bottom: ${theme.spacing.lg};
  }

  h4 {
    font-size: ${theme.typography.fontSize.lg};
    margin: ${theme.spacing.lg} 0 ${theme.spacing.md};
  }
`;

const Form = styled.form`
  background: rgba(255, 255, 255, 0.95);
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.xl};
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
  margin-top: ${theme.spacing['2xl']};
`;

const FormTitle = styled.h3`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize['2xl']};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.lg};
  text-align: center;
`;

const FormGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: ${theme.spacing.lg};

  @media (max-width: ${theme.breakpoints.md}) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  margin-bottom: ${theme.spacing.lg};
`;

const Label = styled.label`
  display: block;
  font-size: ${theme.typography.fontSize.base};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.xs};
`;

const Input = styled.input`
  width: 100%;
  padding: ${theme.spacing.sm};
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.base};

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary.coral};
  }
`;

const Select = styled.select`
  width: 100%;
  padding: ${theme.spacing.sm};
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.base};

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary.coral};
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: ${theme.spacing.sm};
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.base};
  min-height: 120px;
  resize: vertical;

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary.coral};
  }
`;

const SubmitButton = styled(motion.button)`
  background: ${theme.colors.primary.coral};
  color: white;
  border: none;
  border-radius: ${theme.borderRadius.full};
  padding: ${theme.spacing.md} ${theme.spacing.xl};
  font-size: ${theme.typography.fontSize.lg};
  cursor: pointer;
  width: 100%;
  max-width: 400px;
  margin: ${theme.spacing.xl} auto 0;
`;

const ProblemSolutionGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: ${theme.spacing.xl};
  margin: ${theme.spacing['2xl']} 0;

  @media (max-width: ${theme.breakpoints.md}) {
    grid-template-columns: 1fr;
  }
`;

const RoadmapCard = styled(Card)`
  h3 {
    color: ${theme.colors.primary.coral};
    margin-bottom: ${theme.spacing.lg};
  }

  ul {
    margin-top: ${theme.spacing.md};
  }
`;

const TechStack = styled.div`
  display: flex;
  flex-wrap: wrap;
  gap: ${theme.spacing.md};
  margin-top: ${theme.spacing.lg};

  span {
  background: ${theme.colors.primary.peach};
  color: white;
    padding: ${theme.spacing.xs} ${theme.spacing.md};
    border-radius: ${theme.borderRadius.full};
    font-size: ${theme.typography.fontSize.sm};
  }
`;

const AudienceSection = styled(Card)`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
  gap: ${theme.spacing.lg};
  text-align: center;

  div {
    padding: ${theme.spacing.lg};
    border-radius: ${theme.borderRadius.lg};
    background: rgba(255, 192, 203, 0.1);
  }

  h4 {
    color: ${theme.colors.primary.coral};
    margin-bottom: ${theme.spacing.md};
  }
`;

const ImpactSection = styled(Card)`
  text-align: center;

  h3 {
    color: ${theme.colors.primary.coral};
    margin-bottom: ${theme.spacing.xl};
  }

  .impact-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: ${theme.spacing.xl};
  }

  .impact-item {
    padding: ${theme.spacing.lg};
    border-radius: ${theme.borderRadius.lg};
    background: rgba(255, 192, 203, 0.1);
  }
`;

const TeamCard = styled(Card)`
  h4 {
    color: ${theme.colors.primary.coral};
    margin-bottom: ${theme.spacing.md};
  }

  .team-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
    gap: ${theme.spacing.xl};
  }

  .team-group {
    background: rgba(255, 192, 203, 0.1);
    padding: ${theme.spacing.lg};
    border-radius: ${theme.borderRadius.lg};
  }

  .team-meta {
    margin-top: ${theme.spacing.md};
    padding-top: ${theme.spacing.md};
    border-top: 1px solid rgba(0, 0, 0, 0.1);
    font-size: ${theme.typography.fontSize.sm};
    color: ${theme.colors.text.secondary};
  }
`;

const MetricsCard = styled(Card)`
  .metrics-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(200px, 1fr));
    gap: ${theme.spacing.xl};
    margin: ${theme.spacing.xl} 0;
  }

  .metric-item {
    text-align: center;
    padding: ${theme.spacing.lg};
    background: rgba(255, 192, 203, 0.1);
    border-radius: ${theme.borderRadius.lg};
  }

  .metric-value {
    font-size: ${theme.typography.fontSize['3xl']};
    color: ${theme.colors.primary.coral};
    font-weight: bold;
    margin-bottom: ${theme.spacing.sm};
  }

  .metric-label {
    color: ${theme.colors.text.secondary};
    font-size: ${theme.typography.fontSize.sm};
  }
`;

const ProjectionCard = styled(Card)`
  .projection-grid {
    display: grid;
    grid-template-columns: repeat(3, 1fr);
    gap: ${theme.spacing.xl};
    margin: ${theme.spacing.xl} 0;

    @media (max-width: ${theme.breakpoints.md}) {
      grid-template-columns: 1fr;
    }
  }

  .year-projection {
    text-align: center;
    padding: ${theme.spacing.xl};
    background: rgba(255, 192, 203, 0.1);
    border-radius: ${theme.borderRadius.lg};
    position: relative;
    overflow: hidden;
  }

  .year-label {
    font-size: ${theme.typography.fontSize.lg};
    color: ${theme.colors.primary.coral};
    margin-bottom: ${theme.spacing.md};
  }

  .projection-list {
    text-align: left;
  }
`;

const CompetitiveCard = styled(Card)`
  .advantage-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(280px, 1fr));
    gap: ${theme.spacing.xl};
    margin: ${theme.spacing.xl} 0;
  }

  .advantage-item {
    padding: ${theme.spacing.lg};
    background: rgba(255, 192, 203, 0.1);
    border-radius: ${theme.borderRadius.lg};
  }

  h4 {
    color: ${theme.colors.primary.coral};
    margin-bottom: ${theme.spacing.md};
  }
`;

const InvestmentTypesCard = styled(Card)`
  .types-grid {
    display: grid;
    grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
    gap: ${theme.spacing.xl};
    margin: ${theme.spacing.xl} 0;
  }

  .type-item {
    background: rgba(255, 192, 203, 0.1);
    padding: ${theme.spacing.xl};
    border-radius: ${theme.borderRadius.lg};
    position: relative;
    overflow: hidden;
  }

  .type-header {
    display: flex;
    align-items: center;
    gap: ${theme.spacing.md};
    margin-bottom: ${theme.spacing.lg};
  }

  .type-icon {
    font-size: ${theme.typography.fontSize['3xl']};
  }

  .type-title {
    color: ${theme.colors.primary.coral};
    font-size: ${theme.typography.fontSize.xl};
    font-weight: bold;
  }

  .investment-range {
    background: ${theme.colors.primary.peach};
    color: white;
