import React from 'react';
import styled from '@emotion/styled';
import { useContent } from '../../contexts/ContentContext';

interface Feature {
  title: string;
  description: string;
  icon: string;
}

const ContentContainer = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h2`
  color: #845EC2;
  margin-bottom: 20px;
  font-size: 20px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  
  label {
    display: block;
    margin-bottom: 8px;
    color: #333;
    font-weight: 500;
  }
  
  input, textarea {
    width: 100%;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 8px;
    font-size: 14px;
    
    &:focus {
      outline: none;
      border-color: #FF6B6B;
      box-shadow: 0 0 0 2px rgba(255, 107, 107, 0.1);
    }
  }
  
  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const FeatureItem = styled.div`
  background: #f8f9fa;
  padding: 20px;
  border-radius: 8px;
  margin-bottom: 20px;
`;

const SaveButton = styled.button`
  background: #FF6B6B;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background: #ff5252;
    transform: translateY(-1px);
  }
  
  &:active {
    transform: translateY(0);
  }
`;

const SuccessMessage = styled.div`
  background: #4CAF50;
  color: white;
  padding: 12px;
  border-radius: 8px;
  margin-top: 20px;
  text-align: center;
`;

const AddButton = styled.button`
  background: #6c757d;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 6px;
  font-size: 14px;
  cursor: pointer;
  margin-bottom: 20px;
  
  &:hover {
    background: #5a6268;
  }
`;

const RemoveButton = styled.button`
  background: #dc3545;
  color: white;
  border: none;
  padding: 8px 16px;
  border-radius: 6px;
  font-size: 14px;
  cursor: pointer;
  margin-top: 10px;
  
  &:hover {
    background: #c82333;
  }
`;

const FuncionalidadesContentEditor: React.FC = () => {
  const { content, updatePageContent } = useContent();
  const [showSuccess, setShowSuccess] = React.useState(false);
  const pageContent = content.funcionalidades;

  const handleMainChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    updatePageContent('funcionalidades', { [name]: value });
  };

  const handleFeatureChange = (index: number, field: string, value: string) => {
    const newFeatures = [...pageContent.features];
    newFeatures[index] = {
      ...newFeatures[index],
      [field]: value
    };
    updatePageContent('funcionalidades', { features: newFeatures });
  };

  const addFeature = () => {
    const newFeatures = [...pageContent.features, {
      title: 'Nova Funcionalidade',
      description: 'Descrição da nova funcionalidade',
      icon: '✨'
    }];
    updatePageContent('funcionalidades', { features: newFeatures });
  };

  const removeFeature = (index: number) => {
    const newFeatures = pageContent.features.filter((_: Feature, i: number) => i !== index);
    updatePageContent('funcionalidades', { features: newFeatures });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  return (
    <ContentContainer>
      <form onSubmit={handleSubmit}>
        <Section>
          <SectionTitle>Conteúdo Principal</SectionTitle>
          <FormGroup>
            <label htmlFor="title">Título da Página</label>
            <input
              type="text"
              id="title"
              name="title"
              value={pageContent.title}
              onChange={handleMainChange}
            />
          </FormGroup>
          <FormGroup>
            <label htmlFor="subtitle">Subtítulo</label>
            <textarea
              id="subtitle"
              name="subtitle"
              value={pageContent.subtitle}
              onChange={handleMainChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Funcionalidades</SectionTitle>
          <AddButton type="button" onClick={addFeature}>
            + Adicionar Funcionalidade
          </AddButton>
          {pageContent.features.map((feature: Feature, index: number) => (
            <FeatureItem key={index}>
              <FormGroup>
                <label>Título da Funcionalidade</label>
                <input
                  type="text"
                  value={feature.title}
                  onChange={(e) => handleFeatureChange(index, 'title', e.target.value)}
                />
              </FormGroup>
              <FormGroup>
                <label>Descrição</label>
                <textarea
                  value={feature.description}
                  onChange={(e) => handleFeatureChange(index, 'description', e.target.value)}
                />
              </FormGroup>
              <FormGroup>
                <label>Ícone (emoji)</label>
                <input
                  type="text"
                  value={feature.icon}
                  onChange={(e) => handleFeatureChange(index, 'icon', e.target.value)}
                  placeholder="✨"
                />
              </FormGroup>
              <RemoveButton type="button" onClick={() => removeFeature(index)}>
                Remover Funcionalidade
              </RemoveButton>
            </FeatureItem>
          ))}
        </Section>

        <SaveButton type="submit">Salvar Alterações</SaveButton>

        {showSuccess && (
          <SuccessMessage>
            Conteúdo salvo com sucesso!
          </SuccessMessage>
        )}
      </form>
    </ContentContainer>
  );
};

export default FuncionalidadesContentEditor;
