import React, { useEffect, useState } from 'react';
import Hero from '../Hero/Hero';
import { WhatIsDavida } from '../About/WhatIsDavida';
import { ForWomen } from '../ForWomen/ForWomen';
import { Community } from '../Community/Community';
import { AppFeatures } from '../Features/AppFeatures';
import { Download } from '../Download/Download';
import { Partners } from '../Partners/Partners';
import { Contact } from '../Contact/Contact';
import type { HomeContent } from '../../pages/Home/types';
import { defaultContent } from '../../pages/Home/types';

const Home: React.FC = () => {
  const [content, setContent] = useState<HomeContent>(defaultContent);

  useEffect(() => {
    const savedContent = localStorage.getItem('homeContent');
    if (savedContent) {
      try {
        const parsedContent = JSON.parse(savedContent);
        setContent(parsedContent);
      } catch (error) {
        console.error('Erro ao carregar conteúdo:', error);
        setContent(defaultContent);
      }
    }
  }, []);

  // Adiciona um listener para mudanças no localStorage
  useEffect(() => {
    const handleStorageChange = (e: StorageEvent) => {
      if (e.key === 'homeContent' && e.newValue) {
        try {
          const parsedContent = JSON.parse(e.newValue);
          setContent(parsedContent);
        } catch (error) {
          console.error('Erro ao atualizar conteúdo:', error);
        }
      }
    };

    window.addEventListener('storage', handleStorageChange);
    return () => window.removeEventListener('storage', handleStorageChange);
  }, []);

  return (
    <>
      <Hero {...content} />
      <WhatIsDavida />
      <ForWomen />
      <Community />
      <AppFeatures />
      <Download />
      <Partners />
      <Contact />
    </>
  );
};

export default Home;
