import React from 'react';
import styled from '@emotion/styled';
import { useContent } from '../../contexts/ContentContext';

interface Section {
  title: string;
  text: string;
  image: string;
}

const ContentContainer = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h2`
  color: #845EC2;
  margin-bottom: 20px;
  font-size: 20px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  
  label {
    display: block;
    margin-bottom: 8px;
    color: #333;
    font-weight: 500;
  }
  
  input, textarea {
    width: 100%;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 8px;
    font-size: 14px;
    
    &:focus {
      outline: none;
      border-color: #FF6B6B;
      box-shadow: 0 0 0 2px rgba(255, 107, 107, 0.1);
    }
  }
  
  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const SectionItem = styled.div`
  background: #f8f9fa;
  padding: 20px;
  border-radius: 8px;
  margin-bottom: 20px;
`;

const SaveButton = styled.button`
  background: #FF6B6B;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background: #ff5252;
    transform: translateY(-1px);
  }
  
  &:active {
    transform: translateY(0);
  }
`;

const SuccessMessage = styled.div`
  background: #4CAF50;
  color: white;
  padding: 12px;
  border-radius: 8px;
  margin-top: 20px;
  text-align: center;
`;

const ParaMulheresContentEditor: React.FC = () => {
  const { content, updatePageContent } = useContent();
  const [showSuccess, setShowSuccess] = React.useState(false);
  const pageContent = content.para_mulheres;

  const handleMainChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    updatePageContent('para_mulheres', { [name]: value });
  };

  const handleSectionChange = (index: number, field: string, value: string) => {
    const newSections = [...pageContent.sections];
    newSections[index] = {
      ...newSections[index],
      [field]: value
    };
    updatePageContent('para_mulheres', { sections: newSections });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  return (
    <ContentContainer>
      <form onSubmit={handleSubmit}>
        <Section>
          <SectionTitle>Conteúdo Principal</SectionTitle>
          <FormGroup>
            <label htmlFor="title">Título da Página</label>
            <input
              type="text"
              id="title"
              name="title"
              value={pageContent.title}
              onChange={handleMainChange}
            />
          </FormGroup>
          <FormGroup>
            <label htmlFor="subtitle">Subtítulo</label>
            <textarea
              id="subtitle"
              name="subtitle"
              value={pageContent.subtitle}
              onChange={handleMainChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Seções</SectionTitle>
          {pageContent.sections.map((section: Section, index: number) => (
            <SectionItem key={index}>
              <FormGroup>
                <label>Título da Seção</label>
                <input
                  type="text"
                  value={section.title}
                  onChange={(e) => handleSectionChange(index, 'title', e.target.value)}
                />
              </FormGroup>
              <FormGroup>
                <label>Texto</label>
                <textarea
                  value={section.text}
                  onChange={(e) => handleSectionChange(index, 'text', e.target.value)}
                />
              </FormGroup>
              <FormGroup>
                <label>URL da Imagem</label>
                <input
                  type="text"
                  value={section.image}
                  onChange={(e) => handleSectionChange(index, 'image', e.target.value)}
                  placeholder="/images/exemplo.jpg"
                />
              </FormGroup>
            </SectionItem>
          ))}
        </Section>

        <SaveButton type="submit">Salvar Alterações</SaveButton>

        {showSuccess && (
          <SuccessMessage>
            Conteúdo salvo com sucesso!
          </SuccessMessage>
        )}
      </form>
    </ContentContainer>
  );
};

export default ParaMulheresContentEditor;
