interface PageContent {
  title: string;
  description: string;
  sections: {
    [key: string]: {
      title?: string;
      text?: string;
      buttonText?: string;
      buttonLink?: string;
      image?: string;
    }
  }
}

const AdminPage = ({ onClose }: { onClose: () => void }) => {
  const [pageContent, setPageContent] = useState<PageContent>({
    title: "Página Principal",
    description: "Página inicial do Davida",
    sections: {
      hero: {
        title: "Transformando Vidas",
        text: "A Davida é mais que uma plataforma digital - é um movimento de transformação e empoderamento feminino.",
        buttonText: "Saiba Mais",
        buttonLink: "/sobre",
        image: "/images/hero-image.jpg"
      },
      about: {
        title: "Sobre Nós",
        text: "Nossa abordagem única integra desenvolvimento pessoal, profissional e espiritual.",
        image: "/images/about-image.jpg"
      },
      features: {
        title: "Funcionalidades",
        text: "Descubra todas as ferramentas que preparamos para você."
      }
    }
  });

  const handleSave = () => {
    // Aqui você implementaria a lógica para salvar no backend
    console.log('Salvando conteúdo:', pageContent);
    onClose();
  };

  return (
    <AdminPageContent>
      <h2>Editar Página</h2>
      
      <ContentSection>
        <h3>Informações Básicas</h3>
        <EditableField>
          <label>Título da Página</label>
          <input
            type="text"
            value={pageContent.title}
            onChange={(e) => setPageContent({
              ...pageContent,
              title: e.target.value
            })}
          />
        </EditableField>
        <EditableField>
          <label>Descrição</label>
          <textarea
            value={pageContent.description}
            onChange={(e) => setPageContent({
              ...pageContent,
              description: e.target.value
            })}
          />
        </EditableField>
      </ContentSection>

      {Object.entries(pageContent.sections).map(([sectionKey, section]) => (
        <ContentSection key={sectionKey}>
          <h3>Seção: {sectionKey}</h3>
          
          {section.title && (
            <EditableField>
              <label>Título</label>
              <input
                type="text"
                value={section.title}
                onChange={(e) => setPageContent({
                  ...pageContent,
                  sections: {
                    ...pageContent.sections,
                    [sectionKey]: {
                      ...section,
                      title: e.target.value
                    }
                  }
                })}
              />
            </EditableField>
          )}

          {section.text && (
            <EditableField>
              <label>Texto</label>
              <textarea
                value={section.text}
                onChange={(e) => setPageContent({
                  ...pageContent,
                  sections: {
                    ...pageContent.sections,
                    [sectionKey]: {
                      ...section,
                      text: e.target.value
                    }
                  }
                })}
              />
            </EditableField>
          )}

          {section.buttonText && (
            <>
              <EditableField>
                <label>Texto do Botão</label>
                <input
                  type="text"
                  value={section.buttonText}
                  onChange={(e) => setPageContent({
                    ...pageContent,
                    sections: {
                      ...pageContent.sections,
                      [sectionKey]: {
                        ...section,
                        buttonText: e.target.value
                      }
                    }
                  })}
                />
              </EditableField>
              <EditableField>
                <label>Link do Botão</label>
                <input
                  type="text"
                  value={section.buttonLink}
                  onChange={(e) => setPageContent({
                    ...pageContent,
                    sections: {
                      ...pageContent.sections,
                      [sectionKey]: {
                        ...section,
                        buttonLink: e.target.value
                      }
                    }
                  })}
                />
              </EditableField>
            </>
          )}

          {section.image && (
            <EditableField>
              <label>Imagem</label>
              <input
                type="file"
                accept="image/*"
                onChange={(e) => {
                  // Aqui você implementaria o upload da imagem
                  console.log('Upload de imagem:', e.target.files?.[0]);
                }}
              />
              <ImagePreview>
                <img src={section.image} alt="Preview" />
              </ImagePreview>
            </EditableField>
          )}
        </ContentSection>
      ))}

      <ButtonGroup>
        <Button onClick={onClose} style={{ background: '#999' }}>
          Cancelar
        </Button>
        <Button onClick={handleSave}>
          Salvar Alterações
        </Button>
      </ButtonGroup>
    </AdminPageContent>
  );
};

