interface AdminProps {
  isAuthenticated?: boolean;
}

const Admin = ({ isAuthenticated: initialIsAuthenticated = false }: AdminProps) => {
  const [isAuth, setIsAuth] = useState(initialIsAuthenticated);
  const [username, setUsername] = useState('');
  const [password, setPassword] = useState('');
  const navigate = useNavigate();

  const handleLogin = (e: React.FormEvent) => {
    e.preventDefault();
    if (username === 'admin' && password === 'admin') {
      setIsAuth(true);
      navigate('/portal/dashboard');
    } else {
      alert('Usuário ou senha incorretos');
    }
  };

  const handleLogout = () => {
    setIsAuth(false);
    setUsername('');
    setPassword('');
    navigate('/portal');
  };

  if (isAuth) {
    return (
      <PageContainer>
        <Dashboard onLogout={handleLogout} />
      </PageContainer>
    );
  }

  return (
    <PageContainer>
      <LeftSection>
        <Logo>
          <img src="/logo-davida.svg" alt="Davida Logo" />
        </Logo>
        <Briefing>
          <h2>Portal Davida</h2>
          <p>
            Bem-vinda ao portal de gestão da Davida. Aqui você pode gerenciar
            o conteúdo da plataforma, acompanhar métricas e nutrir nossa
            comunidade com conteúdo transformador.
          </p>
          <InvestorsButton onClick={() => navigate('/investidores')}>
            Área de Investidores
          </InvestorsButton>
        </Briefing>
      </LeftSection>
      <RightSection>
        <LoginContainer>
          <Title>Portal Davida</Title>
          <form onSubmit={handleLogin}>
            <Input
              type="text"
              placeholder="Usuário"
              value={username}
              onChange={(e) => setUsername(e.target.value)}
            />
            <Input
              type="password"
              placeholder="Senha"
              value={password}
              onChange={(e) => setPassword(e.target.value)}
            />
            <Button type="submit">Entrar</Button>
          </form>
        </LoginContainer>
      </RightSection>
    </PageContainer>
  );
};

export default Admin;
