import React, { useState } from 'react';
import styled from '@emotion/styled';
import { Link, Routes, Route, useNavigate } from 'react-router-dom';
import PortalContentEditor from './Portal/PortalContent';
import DownloadContentEditor from './Download/DownloadContent';
import HomeContentEditor from './Home/HomeContent';
import ParaMulheresContentEditor from './ParaMulheres/ParaMulheresContent';

const DashboardLayout = styled.div`
  display: flex;
  min-height: 100vh;
`;

const Sidebar = styled.div`
  width: 250px;
  background-color: #2c3e50;
  color: white;
  padding: 20px;
`;

const Content = styled.div`
  flex: 1;
  padding: 20px;
  background-color: #f5f6fa;
`;

const MenuItem = styled(Link)`
  display: block;
  padding: 12px 20px;
  color: white;
  text-decoration: none;
  border-radius: 8px;
  margin-bottom: 8px;
  transition: background-color 0.2s;

  &:hover {
    background-color: rgba(255, 255, 255, 0.1);
  }

  &.active {
    background-color: rgba(255, 255, 255, 0.2);
  }
`;

const Logo = styled.div`
  font-size: 24px;
  font-weight: bold;
  margin-bottom: 40px;
  padding: 0 20px;
`;

const menuItems = [
  { title: 'Home', path: '/admin/home' },
  { title: 'Portal', path: '/admin/portal' },
  { title: 'Download', path: '/admin/download' },
  { title: 'Para Mulheres', path: '/admin/para-mulheres' },
  { title: 'Funcionalidades', path: '/admin/funcionalidades' },
  { title: 'Comunidade', path: '/admin/comunidade' },
  { title: 'Parceiros', path: '/admin/parceiros' },
  { title: 'Contato', path: '/admin/contato' }
];

const Dashboard: React.FC = () => {
  return (
    <DashboardLayout>
      <Sidebar>
        <Logo>Davida Admin</Logo>
        {menuItems.map((item) => (
          <MenuItem key={item.path} to={item.path}>
            {item.title}
          </MenuItem>
        ))}
      </Sidebar>
      <Content>
        <Routes>
          <Route path="/home" element={<HomeContentEditor />} />
          <Route path="/portal" element={<PortalContentEditor />} />
          <Route path="/download" element={<DownloadContentEditor />} />
          <Route path="/para-mulheres" element={<ParaMulheresContentEditor />} />
          <Route path="/funcionalidades" element={<div>Em breve</div>} />
          <Route path="/comunidade" element={<div>Em breve</div>} />
          <Route path="/parceiros" element={<div>Em breve</div>} />
          <Route path="/contato" element={<div>Em breve</div>} />
        </Routes>
      </Content>
    </DashboardLayout>
  );
};

export default Dashboard;
