import React from 'react';
import styled from '@emotion/styled';
import { motion } from 'framer-motion';
import { Helmet } from 'react-helmet-async';
import { theme } from '../../styles/theme';
import EditableContent from '../EditableContent';

const Section = styled.section`
  padding: ${theme.spacing['2xl']} ${theme.spacing.xl};
  background: linear-gradient(135deg, ${theme.colors.primary.peach} 0%, ${theme.colors.primary.rose} 100%);
  min-height: 100vh;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding-top: ${theme.spacing['2xl']};
`;

const Title = styled.h1`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize['4xl']};
  color: white;
  text-align: center;
  margin-bottom: ${theme.spacing.md};
`;

const Subtitle = styled.h2`
  font-size: ${theme.typography.fontSize.xl};
  color: rgba(255, 255, 255, 0.9);
  text-align: center;
  max-width: 800px;
  margin: 0 auto ${theme.spacing.xl};
  line-height: 1.6;
`;

const StatsSection = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: ${theme.spacing.xl};
  margin: ${theme.spacing['2xl']} 0;
`;

const StatCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.95);
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.lg};
  text-align: center;
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
`;

const StatNumber = styled.div`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize['3xl']};
  color: ${theme.colors.primary.coral};
  margin-bottom: ${theme.spacing.sm};
`;

const StatLabel = styled.div`
  font-size: ${theme.typography.fontSize.lg};
  color: ${theme.colors.text.secondary};
`;

const PlansGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(300px, 1fr));
  gap: ${theme.spacing.xl};
  margin: ${theme.spacing['2xl']} 0;
`;

interface PlanCardProps {
  featured?: boolean;
}

const PlanCard = styled(motion.div)<PlanCardProps>`
  background: rgba(255, 255, 255, 0.95);
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.xl};
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
  display: flex;
  flex-direction: column;
  gap: ${theme.spacing.lg};
  position: relative;
  overflow: hidden;

  ${props => props.featured && `
    border: 2px solid ${theme.colors.primary.coral};
    transform: scale(1.02);
    box-shadow: 0 8px 32px rgba(0, 0, 0, 0.15);
  `}
`;

const PlanBadge = styled.div`
  position: absolute;
  top: ${theme.spacing.md};
  right: -${theme.spacing['2xl']};
  background: ${theme.colors.primary.coral};
  color: white;
  padding: ${theme.spacing.xs} ${theme.spacing['2xl']};
  transform: rotate(45deg);
  font-size: ${theme.typography.fontSize.sm};
`;

const PlanTitle = styled.h3`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize['2xl']};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.md};
`;

const PlanDescription = styled.p`
  font-size: ${theme.typography.fontSize.base};
  color: ${theme.colors.text.secondary};
  line-height: 1.6;
  margin-bottom: ${theme.spacing.lg};
`;

const FeaturesList = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;
  flex-grow: 1;
`;

const FeatureItem = styled.li`
  font-size: ${theme.typography.fontSize.base};
  color: ${theme.colors.text.secondary};
  margin-bottom: ${theme.spacing.sm};
  display: flex;
  align-items: center;
  gap: ${theme.spacing.sm};

  &:before {
    content: "✓";
    color: ${theme.colors.primary.coral};
    font-weight: bold;
  }
`;

const IndicatedFor = styled.div`
  margin-top: ${theme.spacing.lg};
  padding-top: ${theme.spacing.lg};
  border-top: 1px solid rgba(0, 0, 0, 0.1);
`;

const IndicatedTitle = styled.h4`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize.lg};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.sm};
`;

const IndicatedText = styled.p`
  font-size: ${theme.typography.fontSize.base};
  color: ${theme.colors.text.secondary};
  line-height: 1.6;
`;

const Form = styled.form`
  background: rgba(255, 255, 255, 0.95);
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.xl};
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
  margin-top: ${theme.spacing['2xl']};
`;

const FormTitle = styled.h3`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize['2xl']};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.lg};
  text-align: center;
`;

const FormGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(2, 1fr);
  gap: ${theme.spacing.lg};

  @media (max-width: ${theme.breakpoints.md}) {
    grid-template-columns: 1fr;
  }
`;

const FormGroup = styled.div`
  margin-bottom: ${theme.spacing.lg};
`;

const Label = styled.label`
  display: block;
  font-size: ${theme.typography.fontSize.base};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.xs};
`;

const Input = styled.input`
  width: 100%;
  padding: ${theme.spacing.sm};
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.base};
  transition: border-color 0.2s;

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary.coral};
  }
`;

const Select = styled.select`
  width: 100%;
  padding: ${theme.spacing.sm};
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.base};
  transition: border-color 0.2s;

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary.coral};
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: ${theme.spacing.sm};
  border: 1px solid rgba(0, 0, 0, 0.1);
  border-radius: ${theme.borderRadius.md};
  font-size: ${theme.typography.fontSize.base};
  min-height: 120px;
  resize: vertical;
  transition: border-color 0.2s;

  &:focus {
    outline: none;
    border-color: ${theme.colors.primary.coral};
  }
`;

const SubmitButton = styled(motion.button)`
  background: ${theme.colors.primary.coral};
  color: white;
  border: none;
  border-radius: ${theme.borderRadius.full};
  padding: ${theme.spacing.md} ${theme.spacing.xl};
  font-size: ${theme.typography.fontSize.lg};
  cursor: pointer;
  transition: background-color 0.2s;
  width: 100%;
  max-width: 400px;
  margin: ${theme.spacing.xl} auto 0;
  display: block;

  &:hover {
    background: ${theme.colors.primary.peach};
  }
`;

const BenefitDescription = styled.p`
  font-size: ${theme.typography.fontSize.base};
  color: ${theme.colors.text.secondary};
  line-height: 1.6;
`;

const BenefitsSection = styled.div`
  margin: ${theme.spacing['2xl']} 0;
`;

const BenefitsGrid = styled.div`
  display: grid;
  grid-template-columns: repeat(auto-fit, minmax(250px, 1fr));
  gap: ${theme.spacing.xl};
`;

const BenefitCard = styled(motion.div)`
  background: rgba(255, 255, 255, 0.95);
  border-radius: ${theme.borderRadius.lg};
  padding: ${theme.spacing.xl};
  box-shadow: 0 4px 16px rgba(0, 0, 0, 0.1);
  text-align: center;
`;

const BenefitIcon = styled.div`
  font-size: ${theme.typography.fontSize['3xl']};
  margin-bottom: ${theme.spacing.md};
`;

const BenefitTitle = styled.h4`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize.xl};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.sm};
`;

export const Partners: React.FC = () => {
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    // Handle form submission
  };

  const plans = [
    {
      title: "Premium",
      featured: true,
      description: "Visibilidade máxima e recursos exclusivos para grandes marcas",
      features: [
        "Página exclusiva com foto ou vídeo institucional",
        "Texto completo de apresentação",
        "Publicação de artigos nos grupos",
        "Link de contato monitorado",
        "Maior destaque nas buscas",
        "Prioridade nos feeds dos grupos",
        "Conteúdo diferenciado e exclusivo"
      ],
      indicatedFor: "Clínicas, médicos, centros de imagem, consultórios multiprofissionais, redes de farmácia, centros de bem-estar e empresas com foco nacional."
    },
    {
      title: "Destaques",
      description: "Visibilidade intermediária ideal para profissionais e pequenas empresas",
      features: [
        "Card com foto e briefing objetivo",
        "Link de contato monitorado",
        "Destaque intermediário na navegação",
        "Visualização ampliada em sessões relacionadas",
        "Participação em campanhas temáticas"
      ],
      indicatedFor: "Pequenas clínicas, profissionais autônomos, consultórios de bairro, lojas especializadas e fornecedores de produtos para mães."
    },
    {
      title: "Regional",
      description: "Presença local otimizada para negócios de bairro",
      features: [
        "Card com imagem e nome comercial",
        "Link com geolocalização",
        "Dados essenciais de contato",
        "Aparecimento em filtros por região",
        "Feed personalizado para usuários locais"
      ],
      indicatedFor: "Negócios locais com atendimento físico ou delivery, como farmácias, laboratórios, doulas e serviços de bairro."
    }
  ];

  const benefits = [
    {
      icon: "🎯",
      title: "Alcance Segmentado",
      description: "Conecte-se com mulheres entre 22-45 anos, em MG, PB e em todo Brasil"
    },
    {
      icon: "💝",
      title: "Ambiente Positivo",
      description: "Sua marca presente em um contexto de acolhimento e propósito"
    },
    {
      icon: "✨",
      title: "Conteúdo Co-branded",
      description: "Desenvolva conteúdo exclusivo e fortaleça sua autoridade"
    },
    {
      icon: "🙏",
      title: "Série de Devocionais",
      description: "Patrocine conteúdo espiritual e conecte-se emocionalmente"
    }
  ];

  return (
    <Section>
      <Helmet>
        <title>Seja Parceiro | Davida - Alie sua marca a um propósito que acolhe vidas</title>
        <meta name="description" content="Conecte sua marca com mulheres que buscam apoio em sua jornada. Alcance segmentado, ambiente positivo e oportunidades únicas de engajamento." />
        <meta name="keywords" content="parceria davida, anúncio premium, visibilidade segmentada, marketing para mulheres, publicidade consciente" />
        <meta property="og:title" content="Seja Parceiro do Davida - Propósito que Transforma" />
        <meta property="og:description" content="Alie sua marca a um propósito que acolhe vidas. Alcance segmentado e ambiente positivo para sua marca." />
        <meta property="og:type" content="website" />
        <link rel="canonical" href="https://davida.app/seja-parceiro" />
      </Helmet>

      <Container>
        <EditableContent
          contentKey="partners.title"
          defaultValue="Seja um Parceiro do Davida"
          type="text"
        />
        <EditableContent
          contentKey="partners.subtitle"
          defaultValue="Junte-se a nós na missão de empoderar mulheres através da tecnologia. Conheça nossos planos de parceria e faça parte desta transformação."
          type="text"
        />

        <StatsSection>
          <StatCard
            whileHover={{ y: -5 }}
            transition={{ duration: 0.2 }}
          >
            <StatNumber>
              <EditableContent
                contentKey="partners.stats.users"
                defaultValue="50,000+"
                type="text"
              />
            </StatNumber>
            <StatLabel>
              <EditableContent
                contentKey="partners.stats.usersLabel"
                defaultValue="Usuárias Ativas"
                type="text"
              />
            </StatLabel>
          </StatCard>

          <StatCard
            whileHover={{ y: -5 }}
            transition={{ duration: 0.2 }}
          >
            <StatNumber>
              <EditableContent
                contentKey="partners.stats.partners"
                defaultValue="100+"
                type="text"
              />
            </StatNumber>
            <StatLabel>
              <EditableContent
                contentKey="partners.stats.partnersLabel"
                defaultValue="Parceiros Ativos"
                type="text"
              />
            </StatLabel>
          </StatCard>

          <StatCard
            whileHover={{ y: -5 }}
            transition={{ duration: 0.2 }}
          >
            <StatNumber>
              <EditableContent
                contentKey="partners.stats.cities"
                defaultValue="30+"
                type="text"
              />
            </StatNumber>
            <StatLabel>
              <EditableContent
                contentKey="partners.stats.citiesLabel"
                defaultValue="Cidades Atendidas"
                type="text"
              />
            </StatLabel>
          </StatCard>
        </StatsSection>

        <PlansGrid>
          <PlanCard
            whileHover={{ y: -5 }}
            transition={{ duration: 0.2 }}
          >
            <PlanTitle>
              <EditableContent
                contentKey="partners.plans.basic.title"
                defaultValue="Plano Básico"
                type="text"
              />
            </PlanTitle>
            <PlanDescription>
              <EditableContent
                contentKey="partners.plans.basic.description"
                defaultValue="Ideal para pequenas empresas que desejam começar sua jornada de impacto social."
                type="text"
              />
            </PlanDescription>
            <FeaturesList>
              <FeatureItem>
                <EditableContent
                  contentKey="partners.plans.basic.feature1"
                  defaultValue="Visibilidade na plataforma"
                  type="text"
                />
              </FeatureItem>
              <FeatureItem>
                <EditableContent
                  contentKey="partners.plans.basic.feature2"
                  defaultValue="Relatórios mensais de impacto"
                  type="text"
                />
              </FeatureItem>
              <FeatureItem>
                <EditableContent
                  contentKey="partners.plans.basic.feature3"
                  defaultValue="Suporte básico"
                  type="text"
                />
              </FeatureItem>
            </FeaturesList>
          </PlanCard>

          <PlanCard
            featured
            whileHover={{ y: -5 }}
            transition={{ duration: 0.2 }}
          >
            <PlanBadge>Mais Popular</PlanBadge>
            <PlanTitle>
              <EditableContent
                contentKey="partners.plans.pro.title"
                defaultValue="Plano Pro"
                type="text"
              />
            </PlanTitle>
            <PlanDescription>
              <EditableContent
                contentKey="partners.plans.pro.description"
                defaultValue="Para empresas comprometidas com impacto social em larga escala."
                type="text"
              />
            </PlanDescription>
            <FeaturesList>
              <FeatureItem>
                <EditableContent
                  contentKey="partners.plans.pro.feature1"
                  defaultValue="Todas as features do Plano Básico"
                  type="text"
                />
              </FeatureItem>
              <FeatureItem>
                <EditableContent
                  contentKey="partners.plans.pro.feature2"
                  defaultValue="Campanhas personalizadas"
                  type="text"
                />
              </FeatureItem>
              <FeatureItem>
                <EditableContent
                  contentKey="partners.plans.pro.feature3"
                  defaultValue="Suporte prioritário"
                  type="text"
                />
              </FeatureItem>
              <FeatureItem>
                <EditableContent
                  contentKey="partners.plans.pro.feature4"
                  defaultValue="Análise de dados avançada"
                  type="text"
                />
              </FeatureItem>
            </FeaturesList>
          </PlanCard>

          <PlanCard
            whileHover={{ y: -5 }}
            transition={{ duration: 0.2 }}
          >
            <PlanTitle>
              <EditableContent
                contentKey="partners.plans.enterprise.title"
                defaultValue="Plano Enterprise"
                type="text"
              />
            </PlanTitle>
            <PlanDescription>
              <EditableContent
                contentKey="partners.plans.enterprise.description"
                defaultValue="Soluções personalizadas para grandes organizações."
                type="text"
              />
            </PlanDescription>
            <FeaturesList>
              <FeatureItem>
                <EditableContent
                  contentKey="partners.plans.enterprise.feature1"
                  defaultValue="Todas as features do Plano Pro"
                  type="text"
                />
              </FeatureItem>
              <FeatureItem>
                <EditableContent
                  contentKey="partners.plans.enterprise.feature2"
                  defaultValue="API dedicada"
                  type="text"
                />
              </FeatureItem>
              <FeatureItem>
                <EditableContent
                  contentKey="partners.plans.enterprise.feature3"
                  defaultValue="Gerente de conta dedicado"
                  type="text"
                />
              </FeatureItem>
              <FeatureItem>
                <EditableContent
                  contentKey="partners.plans.enterprise.feature4"
                  defaultValue="Customização total"
                  type="text"
                />
              </FeatureItem>
            </FeaturesList>
          </PlanCard>
        </PlansGrid>

        <Form onSubmit={handleSubmit}>
          <FormTitle>
            <EditableContent
              contentKey="partners.form.title"
              defaultValue="Entre em Contato"
              type="text"
            />
          </FormTitle>
          <FormGrid>
            <FormGroup>
              <Label>
                <EditableContent
                  contentKey="partners.form.nameLabel"
                  defaultValue="Nome"
                  type="text"
                />
              </Label>
              <Input type="text" required />
            </FormGroup>
            <FormGroup>
              <Label>
                <EditableContent
                  contentKey="partners.form.emailLabel"
                  defaultValue="E-mail"
                  type="text"
                />
              </Label>
              <Input type="email" required />
            </FormGroup>
            <FormGroup>
              <Label>
                <EditableContent
                  contentKey="partners.form.companyLabel"
                  defaultValue="Empresa"
                  type="text"
                />
              </Label>
              <Input type="text" required />
            </FormGroup>
            <FormGroup>
              <Label>
                <EditableContent
                  contentKey="partners.form.planLabel"
                  defaultValue="Plano de Interesse"
                  type="text"
                />
              </Label>
              <Select required>
                <option value="">Selecione um plano</option>
                <option value="basic">Plano Básico</option>
                <option value="pro">Plano Pro</option>
                <option value="enterprise">Plano Enterprise</option>
              </Select>
            </FormGroup>
          </FormGrid>
          <FormGroup>
            <Label>
              <EditableContent
                contentKey="partners.form.messageLabel"
                defaultValue="Mensagem"
                type="text"
              />
            </Label>
            <TextArea required />
          </FormGroup>
          <SubmitButton
            type="submit"
            whileHover={{ scale: 1.02 }}
            whileTap={{ scale: 0.98 }}
          >
            <EditableContent
              contentKey="partners.form.submitButton"
              defaultValue="Enviar Mensagem"
              type="text"
            />
          </SubmitButton>
        </Form>
      </Container>
    </Section>
  );
};
