import React from 'react';
import styled from '@emotion/styled';
import { useContent } from '../../contexts/ContentContext';

const Container = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h3`
  color: #845EC2;
  margin-bottom: 20px;
  font-size: 18px;
`;

const EditableField = styled.div`
  margin-bottom: 20px;

  label {
    display: block;
    font-size: 14px;
    color: #666;
    margin-bottom: 8px;
  }

  input, textarea {
    width: 100%;
    padding: 10px;
    border: 1px solid #ddd;
    border-radius: 4px;
    font-size: 14px;

    &:focus {
      outline: none;
      border-color: #FF6B6B;
    }
  }

  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const Button = styled.button`
  background: #FF6B6B;
  color: white;
  border: none;
  padding: 10px 20px;
  border-radius: 4px;
  cursor: pointer;
  font-size: 14px;
  transition: background-color 0.2s;

  &:hover {
    background: #ff5252;
  }
`;

const ComunidadeContent: React.FC = () => {
  const { content, updateContent } = useContent();
  const [localContent, setLocalContent] = React.useState(content);

  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    setLocalContent(prev => ({ ...prev, [name]: value }));
  };

  const handleSave = () => {
    Object.entries(localContent).forEach(([key, value]) => {
      if (value !== content[key]) {
        updateContent(key, value);
      }
    });
  };

  return (
    <Container>
      <Section>
        <SectionTitle>Seção Principal</SectionTitle>
        <EditableField>
          <label>Título</label>
          <input
            type="text"
            name="community.title"
            value={localContent['community.title'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
        <EditableField>
          <label>Subtítulo</label>
          <textarea
            name="community.subtitle"
            value={localContent['community.subtitle'] || ''}
            onChange={handleInputChange}
          />
        </EditableField>
      </Section>

      <Section>
        <SectionTitle>Depoimentos</SectionTitle>
        {[1, 2, 3].map((index) => (
          <React.Fragment key={index}>
            <EditableField>
              <label>{`Depoimento ${index} - Nome`}</label>
              <input
                type="text"
                name={`community.testimonial${index}.name`}
                value={localContent[`community.testimonial${index}.name`] || ''}
                onChange={handleInputChange}
              />
            </EditableField>
            <EditableField>
              <label>{`Depoimento ${index} - Texto`}</label>
              <textarea
                name={`community.testimonial${index}.text`}
                value={localContent[`community.testimonial${index}.text`] || ''}
                onChange={handleInputChange}
              />
            </EditableField>
          </React.Fragment>
        ))}
      </Section>

      <Section>
        <SectionTitle>Estatísticas</SectionTitle>
        {[1, 2, 3].map((index) => (
          <React.Fragment key={index}>
            <EditableField>
              <label>{`Estatística ${index} - Número`}</label>
              <input
                type="text"
                name={`community.stat${index}.number`}
                value={localContent[`community.stat${index}.number`] || ''}
                onChange={handleInputChange}
              />
            </EditableField>
            <EditableField>
              <label>{`Estatística ${index} - Descrição`}</label>
              <input
                type="text"
                name={`community.stat${index}.description`}
                value={localContent[`community.stat${index}.description`] || ''}
                onChange={handleInputChange}
              />
            </EditableField>
          </React.Fragment>
        ))}
      </Section>

      <Button onClick={handleSave}>Salvar Alterações</Button>
    </Container>
  );
};

export default ComunidadeContent;
