import React from 'react';
import styled from '@emotion/styled';
import { useContent } from '../../contexts/ContentContext';

interface SocialMedia {
  facebook: string;
  instagram: string;
  linkedin: string;
}

const ContentContainer = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h2`
  color: #845EC2;
  margin-bottom: 20px;
  font-size: 20px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  
  label {
    display: block;
    margin-bottom: 8px;
    color: #333;
    font-weight: 500;
  }
  
  input, textarea {
    width: 100%;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 8px;
    font-size: 14px;
    
    &:focus {
      outline: none;
      border-color: #FF6B6B;
      box-shadow: 0 0 0 2px rgba(255, 107, 107, 0.1);
    }
  }
  
  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const SaveButton = styled.button`
  background: #FF6B6B;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background: #ff5252;
    transform: translateY(-1px);
  }
  
  &:active {
    transform: translateY(0);
  }
`;

const SuccessMessage = styled.div`
  background: #4CAF50;
  color: white;
  padding: 12px;
  border-radius: 8px;
  margin-top: 20px;
  text-align: center;
`;

const ContatoContentEditor: React.FC = () => {
  const { content, updatePageContent } = useContent();
  const [showSuccess, setShowSuccess] = React.useState(false);
  const pageContent = content.contato;

  const handleMainChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    updatePageContent('contato', { [name]: value });
  };

  const handleSocialMediaChange = (field: keyof SocialMedia, value: string) => {
    updatePageContent('contato', {
      socialMedia: {
        ...pageContent.socialMedia,
        [field]: value
      }
    });
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  return (
    <ContentContainer>
      <form onSubmit={handleSubmit}>
        <Section>
          <SectionTitle>Conteúdo Principal</SectionTitle>
          <FormGroup>
            <label htmlFor="title">Título da Página</label>
            <input
              type="text"
              id="title"
              name="title"
              value={pageContent.title}
              onChange={handleMainChange}
            />
          </FormGroup>
          <FormGroup>
            <label htmlFor="subtitle">Subtítulo</label>
            <textarea
              id="subtitle"
              name="subtitle"
              value={pageContent.subtitle}
              onChange={handleMainChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Informações de Contato</SectionTitle>
          <FormGroup>
            <label htmlFor="address">Endereço</label>
            <input
              type="text"
              id="address"
              name="address"
              value={pageContent.address}
              onChange={handleMainChange}
              placeholder="Rua Exemplo, 123 - São Paulo/SP"
            />
          </FormGroup>
          <FormGroup>
            <label htmlFor="email">E-mail</label>
            <input
              type="email"
              id="email"
              name="email"
              value={pageContent.email}
              onChange={handleMainChange}
              placeholder="contato@davida.com"
            />
          </FormGroup>
          <FormGroup>
            <label htmlFor="phone">Telefone</label>
            <input
              type="tel"
              id="phone"
              name="phone"
              value={pageContent.phone}
              onChange={handleMainChange}
              placeholder="(11) 1234-5678"
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Redes Sociais</SectionTitle>
          <FormGroup>
            <label>Facebook</label>
            <input
              type="url"
              value={pageContent.socialMedia.facebook}
              onChange={(e) => handleSocialMediaChange('facebook', e.target.value)}
              placeholder="https://facebook.com/davida"
            />
          </FormGroup>
          <FormGroup>
            <label>Instagram</label>
            <input
              type="url"
              value={pageContent.socialMedia.instagram}
              onChange={(e) => handleSocialMediaChange('instagram', e.target.value)}
              placeholder="https://instagram.com/davida"
            />
          </FormGroup>
          <FormGroup>
            <label>LinkedIn</label>
            <input
              type="url"
              value={pageContent.socialMedia.linkedin}
              onChange={(e) => handleSocialMediaChange('linkedin', e.target.value)}
              placeholder="https://linkedin.com/company/davida"
            />
          </FormGroup>
        </Section>

        <SaveButton type="submit">Salvar Alterações</SaveButton>

        {showSuccess && (
          <SuccessMessage>
            Conteúdo salvo com sucesso!
          </SuccessMessage>
        )}
      </form>
    </ContentContainer>
  );
};

export default ContatoContentEditor;
