import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';

const EditorContainer = styled.div`
  background: rgba(255, 255, 255, 0.95);
  border-radius: 12px;
  padding: 20px;
  margin: 20px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 24px;
  padding: 16px;
  border: 1px solid #eee;
  border-radius: 8px;
`;

const SectionTitle = styled.h3`
  color: #845EC2;
  margin-bottom: 16px;
  font-size: 18px;
`;

const EditableInput = styled.input`
  width: 100%;
  padding: 8px 12px;
  border: 1px solid #ddd;
  border-radius: 4px;
  margin-bottom: 8px;
  font-size: 14px;

  &:focus {
    outline: none;
    border-color: #FF6B6B;
    box-shadow: 0 0 0 2px rgba(255, 107, 107, 0.1);
  }
`;

const EditableTextarea = styled.textarea`
  width: 100%;
  padding: 8px 12px;
  border: 1px solid #ddd;
  border-radius: 4px;
  margin-bottom: 8px;
  min-height: 100px;
  font-size: 14px;
  resize: vertical;

  &:focus {
    outline: none;
    border-color: #FF6B6B;
    box-shadow: 0 0 0 2px rgba(255, 107, 107, 0.1);
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 16px;
`;

const Button = styled.button`
  padding: 8px 16px;
  border: none;
  border-radius: 4px;
  font-size: 14px;
  cursor: pointer;
  transition: all 0.2s;

  &.primary {
    background: #FF6B6B;
    color: white;

    &:hover {
      background: #ff5252;
    }
  }

  &.secondary {
    background: #eee;
    color: #666;

    &:hover {
      background: #e0e0e0;
    }
  }
`;

const ImagePreview = styled.div`
  margin: 8px 0;
  
  img {
    max-width: 200px;
    height: auto;
    border-radius: 4px;
    border: 1px solid #ddd;
  }
`;

interface PageContent {
  texts: { [key: string]: string };
  buttons: Array<{ text: string; link: string }>;
  images: Array<{ url: string; alt: string }>;
}

interface DashboardEditorProps {
  pagePath: string;
  onSave: (content: PageContent) => void;
}

const DashboardEditor: React.FC<DashboardEditorProps> = ({ pagePath, onSave }) => {
  const [content, setContent] = useState<PageContent>({
    texts: {},
    buttons: [],
    images: []
  });

  useEffect(() => {
    // TODO: Fetch current page content based on pagePath
    // This would be replaced with your actual API call
    const fetchPageContent = async () => {
      try {
        // Simulated API call
        const response = await fetch(`/api/pages/${pagePath}`);
        const data = await response.json();
        setContent(data);
      } catch (error) {
        console.error('Error fetching page content:', error);
      }
    };

    fetchPageContent();
  }, [pagePath]);

  const handleTextChange = (key: string, value: string) => {
    setContent(prev => ({
      ...prev,
      texts: {
        ...prev.texts,
        [key]: value
      }
    }));
  };

  const handleButtonChange = (index: number, field: 'text' | 'link', value: string) => {
    setContent(prev => ({
      ...prev,
      buttons: prev.buttons.map((button, i) => 
        i === index ? { ...button, [field]: value } : button
      )
    }));
  };

  const handleImageChange = (index: number, field: 'url' | 'alt', value: string) => {
    setContent(prev => ({
      ...prev,
      images: prev.images.map((image, i) => 
        i === index ? { ...image, [field]: value } : image
      )
    }));
  };

  const handleSave = async () => {
    try {
      // TODO: Implement actual save functionality
      await onSave(content);
      alert('Conteúdo salvo com sucesso!');
    } catch (error) {
      console.error('Error saving content:', error);
      alert('Erro ao salvar o conteúdo. Por favor, tente novamente.');
    }
  };

  return (
    <EditorContainer>
      <Section>
        <SectionTitle>Textos</SectionTitle>
        {Object.entries(content.texts).map(([key, value]) => (
          <div key={key}>
            <label>{key}</label>
            {value.length > 100 ? (
              <EditableTextarea
                value={value}
                onChange={(e) => handleTextChange(key, e.target.value)}
              />
            ) : (
              <EditableInput
                value={value}
                onChange={(e) => handleTextChange(key, e.target.value)}
              />
            )}
          </div>
        ))}
      </Section>

      <Section>
        <SectionTitle>Botões</SectionTitle>
        {content.buttons.map((button, index) => (
          <div key={index}>
            <EditableInput
              placeholder="Texto do botão"
              value={button.text}
              onChange={(e) => handleButtonChange(index, 'text', e.target.value)}
            />
            <EditableInput
              placeholder="Link do botão"
              value={button.link}
              onChange={(e) => handleButtonChange(index, 'link', e.target.value)}
            />
          </div>
        ))}
      </Section>

      <Section>
        <SectionTitle>Imagens</SectionTitle>
        {content.images.map((image, index) => (
          <div key={index}>
            <EditableInput
              placeholder="URL da imagem"
              value={image.url}
              onChange={(e) => handleImageChange(index, 'url', e.target.value)}
            />
            <EditableInput
              placeholder="Texto alternativo"
              value={image.alt}
              onChange={(e) => handleImageChange(index, 'alt', e.target.value)}
            />
            <ImagePreview>
              <img src={image.url} alt={image.alt} />
            </ImagePreview>
          </div>
        ))}
      </Section>

      <ButtonGroup>
        <Button className="primary" onClick={handleSave}>
          Salvar Alterações
        </Button>
        <Button className="secondary" onClick={() => window.location.reload()}>
          Cancelar
        </Button>
      </ButtonGroup>
    </EditorContainer>
  );
};

export default DashboardEditor;
