import React, { createContext, useContext, useState, useEffect } from 'react';

interface ContentContextData {
  content: Record<string, any>;
  updateContent: (key: string, value: any) => void;
  updatePageContent: (page: string, content: any) => void;
}

const defaultContent = {
  // Home page content
  heroTitle: 'Empoderamento Feminino',
  heroSubtitle: 'Conectando mulheres, fortalecendo sonhos',
  aboutTitle: 'Sobre a Davida',
  aboutText: 'Uma plataforma dedicada ao empoderamento feminino, oferecendo recursos, suporte e conex√µes para mulheres alcan√ßarem seu potencial m√°ximo.',
  missionTitle: 'Nossa Miss√£o',
  missionText: 'Capacitar mulheres atrav√©s de tecnologia, educa√ß√£o e comunidade.',
  contactTitle: 'Entre em Contato',
  contactText: 'Estamos aqui para ajudar voc√™ em sua jornada.',

  // Para Mulheres page content
  para_mulheres: {
    title: 'Para Mulheres',
    subtitle: 'Um espa√ßo dedicado ao seu crescimento',
    sections: [
      {
        title: 'Desenvolvimento Pessoal',
        text: 'Cursos, workshops e recursos para seu desenvolvimento.',
        image: '/images/desenvolvimento.jpg'
      },
      {
        title: 'Sa√∫de e Bem-estar',
        text: 'Dicas e orienta√ß√µes para uma vida mais saud√°vel.',
        image: '/images/saude.jpg'
      },
      {
        title: 'Carreira',
        text: 'Oportunidades e mentoria para crescimento profissional.',
        image: '/images/carreira.jpg'
      }
    ]
  },

  // Funcionalidades page content
  funcionalidades: {
    title: 'Funcionalidades',
    subtitle: 'Ferramentas para seu sucesso',
    features: [
      {
        title: 'Mentoria Online',
        description: 'Conecte-se com mentoras experientes.',
        icon: 'üë©‚Äçüíº'
      },
      {
        title: 'Cursos Exclusivos',
        description: 'Aprenda no seu ritmo com conte√∫do de qualidade.',
        icon: 'üìö'
      },
      {
        title: 'Networking',
        description: 'Expanda sua rede de contatos profissionais.',
        icon: 'ü§ù'
      }
    ]
  },

  // Comunidade page content
  comunidade: {
    title: 'Nossa Comunidade',
    subtitle: 'Fa√ßa parte desta rede de apoio',
    sections: [
      {
        title: 'Grupos de Discuss√£o',
        text: 'Participe de conversas enriquecedoras.',
        image: '/images/grupos.jpg'
      },
      {
        title: 'Eventos',
        text: 'Encontros presenciais e online.',
        image: '/images/eventos.jpg'
      }
    ],
    testimonials: [
      {
        name: 'Maria Silva',
        text: 'A Davida mudou minha vida profissional.',
        image: '/images/testimonial1.jpg'
      }
    ]
  },

  // Parceiros page content
  parceiros: {
    title: 'Nossos Parceiros',
    subtitle: 'Empresas que acreditam no poder feminino',
    partners: [
      {
        name: 'Empresa A',
        description: 'L√≠der em tecnologia',
        logo: '/images/partner1.jpg',
        link: 'https://empresaa.com'
      }
    ],
    becomePartner: {
      title: 'Seja um Parceiro',
      text: 'Junte-se a n√≥s nesta miss√£o'
    }
  },

  // Contato page content
  contato: {
    title: 'Entre em Contato',
    subtitle: 'Estamos aqui para ajudar',
    address: 'Rua Exemplo, 123 - S√£o Paulo/SP',
    email: 'contato@davida.com',
    phone: '(11) 1234-5678',
    socialMedia: {
      facebook: 'https://facebook.com/davida',
      instagram: 'https://instagram.com/davida',
      linkedin: 'https://linkedin.com/company/davida'
    }
  }
};

const ContentContext = createContext<ContentContextData>({} as ContentContextData);

export const useContent = () => {
  return useContext(ContentContext);
};

export const ContentProvider: React.FC<{ children: React.ReactNode }> = ({ children }) => {
  const [content, setContent] = useState<Record<string, any>>(() => {
    const savedContent = localStorage.getItem('davida-content');
    return savedContent ? JSON.parse(savedContent) : defaultContent;
  });

  useEffect(() => {
    localStorage.setItem('davida-content', JSON.stringify(content));
  }, [content]);

  const updateContent = (key: string, value: any) => {
    setContent(prev => ({ ...prev, [key]: value }));
  };

  const updatePageContent = (page: string, newContent: any) => {
    setContent(prev => ({
      ...prev,
      [page]: {
        ...prev[page],
        ...newContent
      }
    }));
  };

  return (
    <ContentContext.Provider value={{ content, updateContent, updatePageContent }}>
      {children}
    </ContentContext.Provider>
  );
};
