import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';

const ContentContainer = styled.div`
  background: rgba(255, 255, 255, 0.95);
  padding: 30px;
  border-radius: 12px;
  box-shadow: 0 4px 6px rgba(0, 0, 0, 0.1);
`;

const Section = styled.div`
  margin-bottom: 30px;
`;

const SectionTitle = styled.h2`
  color: #845EC2;
  margin-bottom: 20px;
  font-size: 20px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
  
  label {
    display: block;
    margin-bottom: 8px;
    color: #333;
    font-weight: 500;
  }
  
  input, textarea {
    width: 100%;
    padding: 12px;
    border: 1px solid #ddd;
    border-radius: 8px;
    font-size: 14px;
    
    &:focus {
      outline: none;
      border-color: #FF6B6B;
      box-shadow: 0 0 0 2px rgba(255, 107, 107, 0.1);
    }
  }
  
  textarea {
    min-height: 100px;
    resize: vertical;
  }
`;

const SaveButton = styled.button`
  background: #FF6B6B;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 8px;
  font-size: 16px;
  font-weight: 500;
  cursor: pointer;
  transition: all 0.2s;
  
  &:hover {
    background: #ff5252;
    transform: translateY(-1px);
  }
  
  &:active {
    transform: translateY(0);
  }
`;

const SuccessMessage = styled.div`
  background: #4CAF50;
  color: white;
  padding: 12px;
  border-radius: 8px;
  margin-top: 20px;
  text-align: center;
`;

export interface ParaMulheresContent {
  heroTitle: string;
  heroSubtitle: string;
  sections: {
    title: string;
    content: string;
    imageUrl: string;
  }[];
  testimonials: {
    name: string;
    role: string;
    content: string;
    imageUrl: string;
  }[];
  ctaTitle: string;
  ctaText: string;
  ctaButtonText: string;
  ctaButtonLink: string;
}

const defaultContent: ParaMulheresContent = {
  heroTitle: "Um espaço dedicado ao seu bem-estar e crescimento espiritual",
  heroSubtitle: "Descubra uma comunidade acolhedora que entende suas necessidades únicas",
  sections: [
    {
      title: "Acolhimento Espiritual",
      content: "Encontre paz e direção através de nossa abordagem única que combina espiritualidade e apoio emocional. Oferecemos um espaço seguro para você explorar sua jornada espiritual durante a maternidade.",
      imageUrl: "/images/acolhimento-espiritual.jpg"
    },
    {
      title: "Grupos de Apoio",
      content: "Conecte-se com outras mulheres que compartilham experiências semelhantes. Nossos grupos são facilitados por mentoras experientes que oferecem suporte e orientação em sua jornada.",
      imageUrl: "/images/grupos-apoio.jpg"
    },
    {
      title: "Recursos Exclusivos",
      content: "Acesse conteúdo personalizado, meditações guiadas, e ferramentas práticas para seu desenvolvimento pessoal e espiritual. Tudo pensado especialmente para mulheres em diferentes fases da vida.",
      imageUrl: "/images/recursos-exclusivos.jpg"
    }
  ],
  testimonials: [
    {
      name: "Maria Silva",
      role: "Mãe de 2 filhos",
      content: "O Davida transformou minha experiência com a maternidade. Encontrei não só apoio prático, mas também um espaço para crescer espiritualmente.",
      imageUrl: "/images/testimonial-1.jpg"
    },
    {
      name: "Ana Santos",
      role: "Gestante de primeira viagem",
      content: "Os grupos de apoio e as meditações me ajudaram a lidar com a ansiedade da gravidez. Me sinto mais preparada e conectada com meu bebê.",
      imageUrl: "/images/testimonial-2.jpg"
    },
    {
      name: "Juliana Costa",
      role: "Tentante há 2 anos",
      content: "Encontrei força e esperança na comunidade Davida. O suporte espiritual fez toda diferença na minha jornada.",
      imageUrl: "/images/testimonial-3.jpg"
    }
  ],
  ctaTitle: "Comece Sua Jornada",
  ctaText: "Junte-se a milhares de mulheres que já encontraram apoio, conexão e crescimento em nossa comunidade.",
  ctaButtonText: "Quero Fazer Parte",
  ctaButtonLink: "/cadastro"
};

const ParaMulheresContentEditor: React.FC = () => {
  const [content, setContent] = useState<ParaMulheresContent>(defaultContent);
  const [showSuccess, setShowSuccess] = useState(false);

  useEffect(() => {
    const savedContent = localStorage.getItem('paraMulheresContent');
    if (savedContent) {
      setContent(JSON.parse(savedContent));
    }
  }, []);

  const handleChange = (e: React.ChangeEvent<HTMLInputElement | HTMLTextAreaElement>) => {
    const { name, value } = e.target;
    
    if (name.includes('.')) {
      const [section, field, index] = name.split('.');
      setContent(prev => {
        const newContent = { ...prev };
        if (section === 'sections' || section === 'testimonials') {
          newContent[section] = [...prev[section]];
          newContent[section][Number(index)] = {
            ...newContent[section][Number(index)],
            [field]: value
          };
        }
        return newContent;
      });
    } else {
      setContent(prev => ({
        ...prev,
        [name]: value
      }));
    }
  };

  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    localStorage.setItem('paraMulheresContent', JSON.stringify(content));
    setShowSuccess(true);
    setTimeout(() => setShowSuccess(false), 3000);
  };

  return (
    <ContentContainer>
      <form onSubmit={handleSubmit}>
        <Section>
          <SectionTitle>Seção Hero</SectionTitle>
          <FormGroup>
            <label htmlFor="heroTitle">Título Principal</label>
            <textarea
              id="heroTitle"
              name="heroTitle"
              value={content.heroTitle}
              onChange={handleChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="heroSubtitle">Subtítulo</label>
            <textarea
              id="heroSubtitle"
              name="heroSubtitle"
              value={content.heroSubtitle}
              onChange={handleChange}
            />
          </FormGroup>
        </Section>

        <Section>
          <SectionTitle>Seções de Conteúdo</SectionTitle>
          {content.sections.map((section, index) => (
            <div key={index} style={{ marginBottom: '30px', paddingBottom: '20px', borderBottom: '1px solid #eee' }}>
              <FormGroup>
                <label htmlFor={`sections.title.${index}`}>Título da Seção {index + 1}</label>
                <input
                  type="text"
                  id={`sections.title.${index}`}
                  name={`sections.title.${index}`}
                  value={section.title}
                  onChange={handleChange}
                />
              </FormGroup>

              <FormGroup>
                <label htmlFor={`sections.content.${index}`}>Conteúdo da Seção {index + 1}</label>
                <textarea
                  id={`sections.content.${index}`}
                  name={`sections.content.${index}`}
                  value={section.content}
                  onChange={handleChange}
                />
              </FormGroup>

              <FormGroup>
                <label htmlFor={`sections.imageUrl.${index}`}>URL da Imagem da Seção {index + 1}</label>
                <input
                  type="text"
                  id={`sections.imageUrl.${index}`}
                  name={`sections.imageUrl.${index}`}
                  value={section.imageUrl}
                  onChange={handleChange}
                />
              </FormGroup>
            </div>
          ))}
        </Section>

        <Section>
          <SectionTitle>Depoimentos</SectionTitle>
          {content.testimonials.map((testimonial, index) => (
            <div key={index} style={{ marginBottom: '30px', paddingBottom: '20px', borderBottom: '1px solid #eee' }}>
              <FormGroup>
                <label htmlFor={`testimonials.name.${index}`}>Nome</label>
                <input
                  type="text"
                  id={`testimonials.name.${index}`}
                  name={`testimonials.name.${index}`}
                  value={testimonial.name}
                  onChange={handleChange}
                />
              </FormGroup>

              <FormGroup>
                <label htmlFor={`testimonials.role.${index}`}>Papel/Função</label>
                <input
                  type="text"
                  id={`testimonials.role.${index}`}
                  name={`testimonials.role.${index}`}
                  value={testimonial.role}
                  onChange={handleChange}
                />
              </FormGroup>

              <FormGroup>
                <label htmlFor={`testimonials.content.${index}`}>Depoimento</label>
                <textarea
                  id={`testimonials.content.${index}`}
                  name={`testimonials.content.${index}`}
                  value={testimonial.content}
                  onChange={handleChange}
                />
              </FormGroup>

              <FormGroup>
                <label htmlFor={`testimonials.imageUrl.${index}`}>URL da Foto</label>
                <input
                  type="text"
                  id={`testimonials.imageUrl.${index}`}
                  name={`testimonials.imageUrl.${index}`}
                  value={testimonial.imageUrl}
                  onChange={handleChange}
                />
              </FormGroup>
            </div>
          ))}
        </Section>

        <Section>
          <SectionTitle>Seção CTA (Call to Action)</SectionTitle>
          <FormGroup>
            <label htmlFor="ctaTitle">Título do CTA</label>
            <input
              type="text"
              id="ctaTitle"
              name="ctaTitle"
              value={content.ctaTitle}
              onChange={handleChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="ctaText">Texto do CTA</label>
            <textarea
              id="ctaText"
              name="ctaText"
              value={content.ctaText}
              onChange={handleChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="ctaButtonText">Texto do Botão</label>
            <input
              type="text"
              id="ctaButtonText"
              name="ctaButtonText"
              value={content.ctaButtonText}
              onChange={handleChange}
            />
          </FormGroup>

          <FormGroup>
            <label htmlFor="ctaButtonLink">Link do Botão</label>
            <input
              type="text"
              id="ctaButtonLink"
              name="ctaButtonLink"
              value={content.ctaButtonLink}
              onChange={handleChange}
            />
          </FormGroup>
        </Section>

        <SaveButton type="submit">Salvar Alterações</SaveButton>

        {showSuccess && (
          <SuccessMessage>
            Conteúdo salvo com sucesso!
          </SuccessMessage>
        )}
      </form>
    </ContentContainer>
  );
};

export default ParaMulheresContentEditor;
