import React from 'react';
import styled from '@emotion/styled';
import { theme } from '../../styles/theme';
import { useContent } from '../../contexts/ContentContext';

const Container = styled.div`
  padding-top: 80px;
`;

const Hero = styled.section`
  background-color: ${theme.colors.primary.peach};
  padding: ${theme.spacing['2xl']} 0;
  text-align: center;
`;

const HeroContent = styled.div`
  max-width: 800px;
  margin: 0 auto;
  padding: 0 ${theme.spacing.xl};
`;

const Title = styled.h1`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize['5xl']};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.lg};
`;

const Subtitle = styled.p`
  font-size: ${theme.typography.fontSize.xl};
  color: ${theme.colors.text.secondary};
  margin-bottom: ${theme.spacing['2xl']};
`;

const Section = styled.section`
  padding: ${theme.spacing['2xl']} ${theme.spacing.xl};
  max-width: 1200px;
  margin: 0 auto;
`;

const SectionTitle = styled.h2`
  font-family: ${theme.typography.fontFamily.heading};
  font-size: ${theme.typography.fontSize['3xl']};
  color: ${theme.colors.text.primary};
  margin-bottom: ${theme.spacing.xl};
  text-align: center;
`;

const Text = styled.p`
  font-size: ${theme.typography.fontSize.lg};
  color: ${theme.colors.text.secondary};
  line-height: 1.6;
  margin-bottom: ${theme.spacing.xl};
`;

export const Home: React.FC = () => {
  const { content } = useContent();

  return (
    <Container>
      <Hero>
        <HeroContent>
          <Title>{content.heroTitle}</Title>
          <Subtitle>{content.heroSubtitle}</Subtitle>
        </HeroContent>
      </Hero>

      <Section>
        <SectionTitle>{content.aboutTitle}</SectionTitle>
        <Text>{content.aboutText}</Text>
      </Section>

      <Section>
        <SectionTitle>{content.missionTitle}</SectionTitle>
        <Text>{content.missionText}</Text>
      </Section>

      <Section>
        <SectionTitle>{content.contactTitle}</SectionTitle>
        <Text>{content.contactText}</Text>
      </Section>
    </Container>
  );
};
