import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';

export interface ParaMulheresContent {
  hero: {
    title: string;
    subtitle: string;
  };
  sections: {
    title: string;
    content: string;
    imageUrl: string;
  }[];
  testimonials: {
    name: string;
    role: string;
    content: string;
    imageUrl: string;
  }[];
}

const defaultContent: ParaMulheresContent = {
  hero: {
    title: "Para Mulheres",
    subtitle: "Um espaço dedicado ao seu bem-estar e crescimento"
  },
  sections: [
    {
      title: "Apoio e Comunidade",
      content: "Encontre suporte em nossa comunidade de mulheres",
      imageUrl: "/images/community.jpg"
    },
    {
      title: "Desenvolvimento Pessoal",
      content: "Recursos e ferramentas para seu crescimento",
      imageUrl: "/images/growth.jpg"
    },
    {
      title: "Saúde e Bem-estar",
      content: "Cuide da sua saúde física e mental",
      imageUrl: "/images/health.jpg"
    }
  ],
  testimonials: [
    {
      name: "Maria Silva",
      role: "Empreendedora",
      content: "O Davida mudou minha vida profissional",
      imageUrl: "/images/testimonial1.jpg"
    },
    {
      name: "Ana Santos",
      role: "Profissional de Saúde",
      content: "Encontrei uma comunidade incrível aqui",
      imageUrl: "/images/testimonial2.jpg"
    },
    {
      name: "Carla Oliveira",
      role: "Estudante",
      content: "As ferramentas de desenvolvimento são fantásticas",
      imageUrl: "/images/testimonial3.jpg"
    }
  ]
};

const EditorContainer = styled.div`
  padding: 30px;
  background: white;
  border-radius: 12px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  max-width: 1200px;
  margin: 40px auto;
`;

const Section = styled.div`
  margin-bottom: 40px;
  padding: 20px;
  background: #FFF5F5;
  border-radius: 8px;
`;

const SectionTitle = styled.h2`
  color: #333;
  margin-bottom: 20px;
  font-size: 24px;
  border-bottom: 2px solid #FF6B6B;
  padding-bottom: 10px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 8px;
  color: #666;
  font-weight: 500;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  margin-bottom: 10px;
  border: 1px solid #ddd;
  border-radius: 6px;
  font-size: 16px;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #845EC2;
    box-shadow: 0 0 0 2px rgba(132, 94, 194, 0.1);
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 12px;
  margin-bottom: 10px;
  border: 1px solid #ddd;
  border-radius: 6px;
  min-height: 120px;
  font-size: 16px;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #845EC2;
    box-shadow: 0 0 0 2px rgba(132, 94, 194, 0.1);
  }
`;

const Button = styled.button`
  background-color: #845EC2;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  font-size: 16px;
  font-weight: 500;
  transition: all 0.3s ease;
  margin-top: 20px;

  &:hover {
    background-color: #6B4C9F;
    transform: translateY(-2px);
  }

  &:active {
    transform: translateY(0);
  }
`;

const ParaMulheresContentEditor: React.FC = () => {
  const [content, setContent] = useState<ParaMulheresContent>(defaultContent);

  useEffect(() => {
    const savedContent = localStorage.getItem('paraMulheresContent');
    if (savedContent) {
      setContent(JSON.parse(savedContent));
    }
  }, []);

  const handleSave = () => {
    localStorage.setItem('paraMulheresContent', JSON.stringify(content));
    alert('Conteúdo salvo com sucesso!');
  };

  const updateHero = (field: keyof typeof content.hero, value: string) => {
    setContent(prev => ({
      ...prev,
      hero: { ...prev.hero, [field]: value }
    }));
  };

  const updateSection = (index: number, field: keyof typeof content.sections[0], value: string) => {
    setContent(prev => ({
      ...prev,
      sections: prev.sections.map((section, i) => 
        i === index ? { ...section, [field]: value } : section
      )
    }));
  };

  const updateTestimonial = (index: number, field: keyof typeof content.testimonials[0], value: string) => {
    setContent(prev => ({
      ...prev,
      testimonials: prev.testimonials.map((testimonial, i) => 
        i === index ? { ...testimonial, [field]: value } : testimonial
      )
    }));
  };

  return (
    <EditorContainer>
      <Section>
        <SectionTitle>Seção Hero</SectionTitle>
        <FormGroup>
          <Label>Título Principal</Label>
          <Input
            value={content.hero.title}
            onChange={(e) => updateHero('title', e.target.value)}
            placeholder="Digite o título principal"
          />
        </FormGroup>
        <FormGroup>
          <Label>Subtítulo</Label>
          <TextArea
            value={content.hero.subtitle}
            onChange={(e) => updateHero('subtitle', e.target.value)}
            placeholder="Digite o subtítulo"
          />
        </FormGroup>
      </Section>

      <Section>
        <SectionTitle>Seções de Conteúdo</SectionTitle>
        {content.sections.map((section, index) => (
          <div key={index} style={{ marginBottom: '30px', padding: '20px', background: 'white', borderRadius: '8px' }}>
            <FormGroup>
              <Label>Título da Seção {index + 1}</Label>
              <Input
                value={section.title}
                onChange={(e) => updateSection(index, 'title', e.target.value)}
                placeholder="Digite o título da seção"
              />
            </FormGroup>
            <FormGroup>
              <Label>Conteúdo da Seção {index + 1}</Label>
              <TextArea
                value={section.content}
                onChange={(e) => updateSection(index, 'content', e.target.value)}
                placeholder="Digite o conteúdo da seção"
              />
            </FormGroup>
            <FormGroup>
              <Label>URL da Imagem</Label>
              <Input
                value={section.imageUrl}
                onChange={(e) => updateSection(index, 'imageUrl', e.target.value)}
                placeholder="Digite a URL da imagem"
              />
            </FormGroup>
          </div>
        ))}
      </Section>

      <Section>
        <SectionTitle>Depoimentos</SectionTitle>
        {content.testimonials.map((testimonial, index) => (
          <div key={index} style={{ marginBottom: '30px', padding: '20px', background: 'white', borderRadius: '8px' }}>
            <FormGroup>
              <Label>Nome</Label>
              <Input
                value={testimonial.name}
                onChange={(e) => updateTestimonial(index, 'name', e.target.value)}
                placeholder="Digite o nome"
              />
            </FormGroup>
            <FormGroup>
              <Label>Cargo/Função</Label>
              <Input
                value={testimonial.role}
                onChange={(e) => updateTestimonial(index, 'role', e.target.value)}
                placeholder="Digite o cargo ou função"
              />
            </FormGroup>
            <FormGroup>
              <Label>Depoimento</Label>
              <TextArea
                value={testimonial.content}
                onChange={(e) => updateTestimonial(index, 'content', e.target.value)}
                placeholder="Digite o depoimento"
              />
            </FormGroup>
            <FormGroup>
              <Label>URL da Foto</Label>
              <Input
                value={testimonial.imageUrl}
                onChange={(e) => updateTestimonial(index, 'imageUrl', e.target.value)}
                placeholder="Digite a URL da foto"
              />
            </FormGroup>
          </div>
        ))}
      </Section>

      <Button onClick={handleSave}>Salvar Alterações</Button>
    </EditorContainer>
  );
};

export default ParaMulheresContentEditor;
