import React, { useState, useEffect } from 'react';
import styled from '@emotion/styled';
import { ParaMulheresContent, defaultContent } from './types';

const EditorContainer = styled.div`
  padding: 30px;
  background: white;
  border-radius: 12px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  max-width: 1200px;
  margin: 40px auto;
`;

const Section = styled.div`
  margin-bottom: 40px;
  padding: 20px;
  background: #FFF5F5;
  border-radius: 8px;
`;

const SectionTitle = styled.h2`
  color: #333;
  margin-bottom: 20px;
  font-size: 24px;
  border-bottom: 2px solid #FF6B6B;
  padding-bottom: 10px;
`;

const FormGroup = styled.div`
  margin-bottom: 20px;
`;

const Label = styled.label`
  display: block;
  margin-bottom: 8px;
  color: #666;
  font-weight: 500;
`;

const Input = styled.input`
  width: 100%;
  padding: 12px;
  margin-bottom: 10px;
  border: 1px solid #ddd;
  border-radius: 6px;
  font-size: 16px;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #845EC2;
    box-shadow: 0 0 0 2px rgba(132, 94, 194, 0.1);
  }
`;

const TextArea = styled.textarea`
  width: 100%;
  padding: 12px;
  margin-bottom: 10px;
  border: 1px solid #ddd;
  border-radius: 6px;
  min-height: 120px;
  font-size: 16px;
  transition: border-color 0.3s ease;

  &:focus {
    outline: none;
    border-color: #845EC2;
    box-shadow: 0 0 0 2px rgba(132, 94, 194, 0.1);
  }
`;

const FeatureList = styled.div`
  margin-top: 10px;
`;

const FeatureItem = styled.div`
  display: flex;
  gap: 10px;
  margin-bottom: 10px;
  align-items: center;
`;

const Button = styled.button`
  background-color: #845EC2;
  color: white;
  padding: 12px 24px;
  border: none;
  border-radius: 6px;
  cursor: pointer;
  font-size: 16px;
  font-weight: 500;
  transition: all 0.3s ease;
  margin-right: 10px;

  &:hover {
    background-color: #6B4C9F;
    transform: translateY(-2px);
  }

  &:active {
    transform: translateY(0);
  }

  &.secondary {
    background-color: #FF6B6B;
    
    &:hover {
      background-color: #FF5252;
    }
  }
`;

const ButtonGroup = styled.div`
  display: flex;
  gap: 10px;
  margin-top: 20px;
`;

const SaveIndicator = styled.div<{ visible: boolean }>`
  position: fixed;
  top: 20px;
  right: 20px;
  background-color: #4CAF50;
  color: white;
  padding: 12px 24px;
  border-radius: 6px;
  opacity: ${props => props.visible ? '1' : '0'};
  transition: opacity 0.3s ease;
  z-index: 1000;
`;

const ParaMulheresContentEditor: React.FC = () => {
  const [content, setContent] = useState<ParaMulheresContent>(defaultContent);
  const [showSaveIndicator, setShowSaveIndicator] = useState(false);
  const [hasChanges, setHasChanges] = useState(false);

  useEffect(() => {
    const savedContent = localStorage.getItem('paraMulheresContent');
    if (savedContent) {
      try {
        const parsedContent = JSON.parse(savedContent);
        setContent(parsedContent);
      } catch (error) {
        console.error('Erro ao carregar conteúdo salvo:', error);
        setContent(defaultContent);
      }
    }
  }, []);

  useEffect(() => {
    if (hasChanges) {
      const saveTimeout = setTimeout(() => {
        handleSave();
      }, 1000);

      return () => clearTimeout(saveTimeout);
    }
  }, [content, hasChanges]);

  const handleSave = () => {
    try {
      localStorage.setItem('paraMulheresContent', JSON.stringify(content));
      setShowSaveIndicator(true);
      setHasChanges(false);
      setTimeout(() => setShowSaveIndicator(false), 2000);
    } catch (error) {
      console.error('Erro ao salvar conteúdo:', error);
      alert('Erro ao salvar o conteúdo. Por favor, tente novamente.');
    }
  };

  const handleReset = () => {
    if (window.confirm('Tem certeza que deseja resetar todo o conteúdo para o padrão? Esta ação não pode ser desfeita.')) {
      setContent(defaultContent);
      localStorage.setItem('paraMulheresContent', JSON.stringify(defaultContent));
      setShowSaveIndicator(true);
      setTimeout(() => setShowSaveIndicator(false), 2000);
    }
  };

  const updateHero = (field: keyof typeof content.hero, value: string) => {
    setContent(prev => ({
      ...prev,
      hero: { ...prev.hero, [field]: value }
    }));
    setHasChanges(true);
  };

  const updateSection = (index: number, field: keyof typeof content.sections[0], value: string | string[]) => {
    setContent(prev => ({
      ...prev,
      sections: prev.sections.map((section, i) => 
        i === index ? { ...section, [field]: value } : section
      )
    }));
    setHasChanges(true);
  };

  const updateFeature = (sectionIndex: number, featureIndex: number, value: string) => {
    setContent(prev => ({
      ...prev,
      sections: prev.sections.map((section, i) => 
        i === sectionIndex 
          ? {
              ...section,
              features: section.features.map((feature, j) => 
                j === featureIndex ? value : feature
              )
            }
          : section
      )
    }));
    setHasChanges(true);
  };

  return (
    <EditorContainer>
      <SaveIndicator visible={showSaveIndicator}>
        Conteúdo salvo com sucesso!
      </SaveIndicator>

      <Section>
        <SectionTitle>Seção Hero</SectionTitle>
        <FormGroup>
          <Label>Título Principal</Label>
          <Input
            value={content.hero.title}
            onChange={(e) => updateHero('title', e.target.value)}
            placeholder="Digite o título principal"
          />
        </FormGroup>
        <FormGroup>
          <Label>Subtítulo</Label>
          <TextArea
            value={content.hero.subtitle}
            onChange={(e) => updateHero('subtitle', e.target.value)}
            placeholder="Digite o subtítulo"
          />
        </FormGroup>
      </Section>

      {content.sections.map((section, sectionIndex) => (
        <Section key={sectionIndex}>
          <SectionTitle>Seção {section.title}</SectionTitle>
          <FormGroup>
            <Label>Ícone</Label>
            <Input
              value={section.icon}
              onChange={(e) => updateSection(sectionIndex, 'icon', e.target.value)}
              placeholder="Digite o emoji do ícone"
            />
          </FormGroup>
          <FormGroup>
            <Label>Título</Label>
            <Input
              value={section.title}
              onChange={(e) => updateSection(sectionIndex, 'title', e.target.value)}
              placeholder="Digite o título da seção"
            />
          </FormGroup>
          <FormGroup>
            <Label>Descrição</Label>
            <TextArea
              value={section.description}
              onChange={(e) => updateSection(sectionIndex, 'description', e.target.value)}
              placeholder="Digite a descrição da seção"
            />
          </FormGroup>
          <FormGroup>
            <Label>URL</Label>
            <Input
              value={section.url}
              onChange={(e) => updateSection(sectionIndex, 'url', e.target.value)}
              placeholder="Digite a URL da seção"
            />
          </FormGroup>
          <FormGroup>
            <Label>Recursos</Label>
            <FeatureList>
              {section.features.map((feature, featureIndex) => (
                <FeatureItem key={featureIndex}>
                  <Input
                    value={feature}
                    onChange={(e) => updateFeature(sectionIndex, featureIndex, e.target.value)}
                    placeholder={`Recurso ${featureIndex + 1}`}
                  />
                </FeatureItem>
              ))}
            </FeatureList>
          </FormGroup>
        </Section>
      ))}

      <ButtonGroup>
        <Button onClick={handleSave}>
          Salvar Alterações
        </Button>
        <Button onClick={handleReset} className="secondary">
          Resetar para Padrão
        </Button>
      </ButtonGroup>
    </EditorContainer>
  );
};

export default ParaMulheresContentEditor;
